class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def shou(a, b, c, d):
            while a < c and all(grid[a][i] == 0 for i in range(b, d + 1)):
                a += 1
            while b < d and all(grid[i][b] == 0 for i in range(a, c + 1)):
                b += 1
            while c > a and all(grid[c][i] == 0 for i in range(b, d + 1)):
                c -= 1
            while d > b and all(grid[i][d] == 0 for i in range(a, c + 1)):
                d -= 1
            return a, b, c, d

        m, n = len(grid), len(grid[0])
        # 切割grid, 返回所有可能情况
        def qie(a, b, c, d):
            res = []
            for i in range(a, c):
                res.append([(a, b, i, d), (i + 1, b, c, d)])
            for i in range(b, d):
                res.append([(a, b, c, i), (a, i + 1, c, d)])
            return res

        qaq = qie(0, 0, m - 1, n - 1)
        res = float('inf')
        def jisuan(x):
            return (x[2] - x[0] + 1) * (x[3] - x[1] + 1)
        for x, y in qaq:
            x, y = shou(*x), shou(*y)

            for orz0, orz1 in qie(*x):
                orz0, orz1 = shou(*orz0), shou(*orz1)
                res = min(res, jisuan(y) + jisuan(orz0) + jisuan(orz1))
            for orz2, orz3 in qie(*y):
                orz2, orz3 = shou(*orz2), shou(*orz3)
                res = min(res, jisuan(x) + jisuan(orz2) + jisuan(orz3))

            # orz0, orz1 = qie(*x)
            # orz2, orz3 = qie(*y)
            # orz0, orz1 = shou(*orz0), shou(*orz1)
            # orz2, orz3 = shou(*orz2), shou(*orz3)
            # res = min(res, jisuan(x) + jisuan(orz2)+ jisuan(orz3), jisuan(y) + jisuan(orz0) + jisuan(orz1))
        return res
