class Solution {
    vector<vector<int>> g;


    int GetArea(int L, int R, int U, int D){
        //int n=g.size(), m=g[0].size();
        int l=R, r = L, u=D, d =U;

        for(int i=L;i<=R;i++){
            for(int j=U;j<=D;j++){
                if(g[i][j]==1){
                    l = min(l, i);
                    r = max(r, i);
                    u=min(u, j);
                    d = max(d, j);
                }
            }
        }
        int area = (r-l+1)*(d-u+1);
        if(area<0)cout<<l<<r<<u<<d<<endl;

        return area;
    }
public:
    int minimumSum(vector<vector<int>>& grid) {
        g=grid;
        int n=g.size(), m=g[0].size();
        int l=n-1, r = 0, u=m-1, d =0;

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                if(g[i][j]==1){
                    l = min(l, i);
                    r = max(r, i);
                    u=min(u, j);
                    d = max(d, j);
                }
            }
        }
        int ans = INT_MAX;



      //  cout<<l<<r<<u<<d;
        for(int i=l+1;i<r;i++){
            for(int j=i;j<r;j++){
                int a = GetArea(l, i-1, u, d);
                int b = GetArea(i, j, u, d);
                int c = GetArea(j+1, r, u, d);
              if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
            //  cout<<a<<b<<c;
                ans = min(ans, a+b+c);
            }
        }

        for(int i=u+1;i<d;i++){
            for(int j=i;j<d;j++){
                int a = GetArea(l, r, u, i-1);
                int b = GetArea(l, r, i, j);
                int c = GetArea(l, r, j+1, d);
                  if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
             // cout<<a<<b<<c<<endl;
                ans = min(ans, a+b+c);
            }
        }

        for(int i=l;i<r;i++){
            // l -> i
            // i+1 -> r
            for(int j=u;j<d;j++){
                // u ->j;
                // j+1 -> d

                int a = GetArea(l, i, u, j);
                int b = GetArea(l,i,j+1,d);
                int c = GetArea(i+1,r,u,d);
                 if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
                ans = min(ans, a+b+c);


                a = GetArea(l, i, u, d);
                b = GetArea(i+1,r,j+1,d);
                c = GetArea(i+1,r,u,j);
                  if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
                ans = min(ans, a+b+c);

                a = GetArea(l, i, u, j);
                b = GetArea(i+1,r,u,j);
                c = GetArea(l,r,j+1,d);
                if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
                ans = min(ans, a+b+c);

                a = GetArea(l, r, u, j);
                b = GetArea(l,i,j+1,d);
                c = GetArea(i+1,r,j+1,d);
                  if(a<=0)a=1;if(b<=0)b=1;if(c<=0)c=1;
                ans = min(ans, a+b+c);
            }
        }

        return ans;

    }
};
