class Solution {
public:
    int n,m;
    int solve(vector<vector<int>>& grid, int start_x, int end_x, int start_y, int end_y) {
        // cout<<start_x<<" "<< end_x<<" "<< start_y<<" "<< end_y<<endl;
        // int start_x = 0;
        // int start_y = 0;
        // int end_x = n-1;
        // int end_y = m-1;
        for( ; start_x<=end_x;start_x++){
            int flag = true;
            for(int j=start_y;j<=end_y;j++){
                if(grid[start_x][j]) {
                    flag = false;
                    break;
                }
            }
            if(!flag) break;
        }
        // if(start_y==1 && end_y==1)
        // cout<<"start_x"<<" "<<start_x<<endl;
        for( ;end_x>=0 && end_x>=start_x;end_x--){
            int flag = true;
            for(int j=start_y;j<=end_y;j++){
                if(grid[end_x][j]) {
                    flag = false;
                    break;
                }
            }
            if(!flag) break;

        }
        // if(start_y==1 && end_y==1)
        // cout<<"end_x"<<" "<<end_x<<endl;
        for( ; start_y<=end_y;start_y++){
            int flag = true;
            for(int i=start_x;i<=end_x;i++){
                if(grid[i][start_y]) {
                    flag = false;
                    break;
                }
            }
            if(!flag) break;
        }
        // if(start_y==1 && end_y==1)
        // cout<<"start_y"<<" "<<start_y<<endl;
        for( ;end_y>=start_y;end_y--){
            int flag = true;
            for(int i=start_x;i<=end_x;i++){
                if(grid[i][end_y]) {
                    flag = false;
                    break;
                }
            }
            if(!flag) break;
        }
        // if(start_y==1 && end_y==1)
        // cout<<"end_y"<<" "<<end_y<<endl;
        return (end_x - start_x+1)*(end_y-start_y+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        n = grid.size();
        m = grid[0].size();
        int ans = INT_MAX;
        for(int i=0;i<n-2;i++){
            for(int j=i+1;j<n-1;j++){
                int a =solve(grid, 0,i,0,m-1 );
                int b =solve(grid, i+1,j,0,m-1);
                int c =solve(grid, j+1,n-1,0,m-1);
                // cout<<a<<" "<<b<<" "<<c<<endl;
                ans = min(ans, a+b+c);
            }
        }
        // cout<<ans<<" "<<endl;
        for(int i=0;i<m-2;i++){
            for(int j=i+1;j<m-1;j++){
                // cout<<i<<" "<<j<<endl;
                int a =solve(grid, 0,n-1,0,i );
                int b =solve(grid, 0,n-1,i+1,j);
                int c =solve(grid, 0,n-1,j+1,m-1);
                // cout<<a<<" "<<b<<" "<<c<<endl;
                ans = min(ans, a+b+c);
            }
        }
        for(int k=0;k<m-1;k++){
            int a =solve(grid, 0,n-1,0,k );
            for(int i=0;i<n-1;i++){


                    int b =solve(grid, 0,i,k+1,m-1);
                    int c =solve(grid, i+1,n-1,k+1,m-1);
                    // cout<<a<<" "<<b<<" "<<c<<endl;
                    ans = min(ans, a+b+c);
            }
        }
        for(int k=m-1;k>0;k--){
            int a =solve(grid, 0,n-1,k,m-1 );
            for(int i=0;i<n-1;i++){


                    int b =solve(grid, 0,i,0,k-1);
                    int c =solve(grid, i+1,n-1,0,k-1);
                    // cout<<a<<" "<<b<<" "<<c<<endl;
                    ans = min(ans, a+b+c);
            }
        }
        for(int k=0;k<n-1;k++){
            int a =solve(grid, 0,k,0,m-1 );
            for(int i=0;i<m-1;i++){
                // cout<<i<<" "<<j<<endl;

                int b =solve(grid, k+1,n-1,0,i);
                int c =solve(grid, k+1,n-1,i+1,m-1);
                // cout<<a<<" "<<b<<" "<<c<<endl;
                ans = min(ans, a+b+c);
            }
        }
        for(int k=n-1;k>0;k--){
            int a =solve(grid, k,n-1,0,m-1 );
            for(int i=0;i<m-1;i++){
                // cout<<i<<" "<<j<<endl;

                int b =solve(grid, 0,k-1,0,i);
                int c =solve(grid, 0,k-1,i+1,m-1);
                // cout<<a<<" "<<b<<" "<<c<<endl;
                ans = min(ans, a+b+c);
            }
        }
        // cout<<ans<<" "<<endl;
        return ans;
    }
};
