class Solution {
    int help(int tli, int tlj, int bri, int brj, vector<vector<int>>&grid){
        int minl=INT_MAX,maxr=0,mint=INT_MAX,maxb=0;
        bool ok = false;
        for(int i=tli;i<=bri;++i){
            for(int j=tlj;j<=brj;++j){
                if(grid[i][j]){
                    ok = true;
                    minl=min(minl, j);
                    maxr=max(maxr, j);
                    mint=min(mint, i);
                    maxb=max(maxb, i);
                }
            }
        }
        if(ok) return (maxb-mint+1)*(maxr-minl+1);
        else return -1;
    }
public:
