class Solution {
public:
    int minimumSum1(int sx, int ex, int sy, int ey, vector<vector<int>>& grid) {
        int mnx = 10001, mny = 100001, mxx = 0, mxy = 0;
        int n = grid.size(), m = grid[0].size();
        bool flag = 1;
        for(int i = sx; i <= ex; i++)
        {
            for(int j = sy; j <= ey; j++)
            {
                if(grid[i][j])
                {
                    flag = 0;
                    mnx = min(mnx, i);
                    mny = min(mny, j);
                    mxx = max(mxx, i);
                    mxy = max(mxy, j);
                }
            }
        }
        if(flag) return 0;
        return (mxx - mnx + 1) * (mxy - mny + 1);
    }
    int minimumSum2(int sx, int ex, int sy, int ey, vector<vector<int>>& grid) {
        int res = 1e9, n = grid.size(), m = grid[0].size();
        for(int i = sx; i <= ex; i++)
        {
            res = min(res, minimumSum1(sx, i, sy, ey, grid) + minimumSum1(i + 1, ex, sy, ey, grid));
        }
        for(int j = sy; j <= ey; j++)
        {
            res = min(res, minimumSum1(sx, ex, sy, j, grid) + minimumSum1(sx, ex, j + 1, ey, grid));
        }
        return res;
    }
    int minimumSum(vector<vector<int>>& grid) {
        int res = 1e9, n = grid.size(), m = grid[0].size();
        int sx = 0, ex = n - 1, sy = 0, ey = m - 1;
        for(int i = 0; i < n; i++)
        {
            res = min(res, minimumSum1(sx, i, sy, ey, grid) + minimumSum2(i + 1, ex, sy, ey, grid));
            res = min(res, minimumSum2(sx, i, sy, ey, grid) + minimumSum1(i + 1, ex, sy, ey, grid));
        }
        for(int j = sy; j <= ey; j++)
        {
            res = min(res, minimumSum2(sx, ex, sy, j, grid) + minimumSum1(sx, ex, j + 1, ey, grid));
            res = min(res, minimumSum1(sx, ex, sy, j, grid) + minimumSum2(sx, ex, j + 1, ey, grid));
        }
        return res;
    }
};
