class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        @cache
        def check(y1: int, x1: int, y2: int, x2: int) -> int:
            top, left, bottm, rigth = inf, inf, 0, 0
            for y in range(y1, y2 + 1):
                for x in range(x1, x2 + 1):
                    if grid[y][x] == 1:
                        top = min(top, y)
                        left = min(left, x)
                        bottm = max(bottm, y)
                        rigth = max(rigth, x)
            # res = (bottm - top + 1) * (rigth - left + 1)
            # return 0 if res == inf or res == -inf else res
            return (bottm - top + 1) * (rigth - left + 1)


        n, m = len(grid), len(grid[0])
        best = inf

        # 横切横分
        for y in range(n - 2):
            for yx in range(y+1, n-1):
                res1 = check(0,0,y,m-1) + check(y+1,0,yx,m-1) + check(yx+1,0,n-1,m-1)
                best = min(best, res1)

        # 竖切竖分
        for x in range(m - 2):
            for xy in range(x+1, m-1):
                res1 = check(0,0,n-1,x) + check(0,x+1,n-1,xy) + check(0,xy+1,n-1,m-1)
                best = min(best, res1)

        for y in range(n - 1):
            for x in range(m - 1):
                # 竖切横分
                for yx in range(n - 1):
                    res1 = check(0,0,yx,x) + check(0,x+1,n-1,m-1) + check(yx+1,0,n-1,x)
                    res2 = check(0,0,n-1,x) + check(0,x+1,yx,m-1) + check(yx+1,x+1,n-1,m-1)
                    # print(res1,res2)
                    best = min(best, res1, res2)
                # 横切竖分
                for xy in range(m - 1):
                    res1 = check(0,0,y,xy) + check(0,xy+1,y,m-1) + check(y+1,0,n-1,m-1)
                    res2 = check(0,0,y,m-1) + check(y+1,0,n-1,xy) + check(y+1,xy+1,n-1,m-1)
                    # print(y,xy,res1,res2)
                    best = min(best, res1, res2)

        return best
