class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])

        @cache
        def min_area(r1: int, r2: int, c1: int, c2: int) -> int:
            lmost, rmost = n, 0
            tmost, bmost = m, 0
            for i in range(r1, r2 + 1):
                for j in range(c1, c2 + 1):
                    if grid[i][j]:
                        lmost = min(lmost, j)
                        rmost = max(rmost, j)
                        tmost = min(tmost, i)
                        bmost = max(bmost, i)

            return max(0, (rmost - lmost + 1) * (bmost - tmost + 1))

        res = m * n
        for i in range(m):

            # another horizontal cut
            for j in range(i + 1, m):
                t0, t1, t2 = min_area(0, i, 0, n - 1), min_area(i + 1, j, 0, n - 1), min_area(j + 1, m - 1, 0, n - 1)
                res = min(res, t0 + t1 + t2)

            # another vertical cut
            for j in range(n):
                t0, t1, t2 = min_area(0, i, 0, n - 1), min_area(i + 1, m - 1, 0, j), min_area(i + 1, m - 1, j + 1, n - 1)
                res = min(res, t0 + t1 + t2)
                t0, t1, t2 = min_area(0, i, 0, j), min_area(0, i, j + 1, n - 1), min_area(i + 1, m - 1, 0, n - 1)
                res = min(res, t0 + t1 + t2)

        for i in range(n):

            # another veritical cut
            for j in range(i + 1, n):
                t0, t1, t2 = min_area(0, m - 1, 0, i), min_area(0, m - 1, i + 1, j), min_area(0, m - 1, j + 1, n - 1)
                res = min(res, t0 + t1 + t2)

            # another horizontal cut
            for j in range(m):
                t0, t1, t2 = min_area(0, m - 1, 0, i), min_area(0, j, i + 1, n - 1), min_area(j + 1, m - 1, i + 1, n - 1)
                res = min(res, t0 + t1 + t2)
                t0, t1, t2 = min_area(0, j, 0, i), min_area(j + 1, m - 1, 0, i), min_area(0, m - 1, i + 1, n - 1)
                res = min(res, t0 + t1 + t2)

        return res
