class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def area(array):
            group = [[inf, inf, -inf, -inf] for _ in range(3)]
            for x, y in pool:
                for index in range(3):
                    if array[index][0] <= x < array[index][2] and array[index][1] <= y < array[index][3]:
                        group[index] = [min(group[index][0], x), min(group[index][1], y), max(group[index][2], x + 1), max(group[index][3], y + 1)]
                        break
            return sum(max(group[index][2] - group[index][0], 0) * max(group[index][3] - group[index][1], 0) for index in range(3))

        m, n, row, col, pool, ans = len(grid), len(grid[0]), [inf, -inf], [inf, -inf], [], inf
        for i in range(m):
            for j in range(n):
                if grid[i][j]:
                    row, col = [min(row[0], i), max(row[1], i + 1)], [min(col[0], j), max(col[1], j + 1)]
                    pool.append([i, j])
        for j1 in range(col[0] + 1, col[1] - 1):
            for j2 in range(j1 + 1, col[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], row[1], j1],
                            [row[0], j1, row[1], j2],
                            [row[0], j2, row[1], col[1]]
                        ]
                    )
                )
        for j in range(col[0] + 1, col[1]):
            for i in range(row[0] + 1, row[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], row[1], j],
                            [row[0], j, i, col[1]],
                            [i, j, row[1], col[1]]
                        ]
                    )
                )
        for i in range(row[0] + 1, row[1]):
            for j in range(col[0] + 1, col[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], i, j],
                            [i, col[0], row[1], j],
                            [row[0], j, row[1], col[1]]
                        ]
                    )
                )
        for i in range(row[0] + 1, row[1]):
            for j in range(col[0] + 1, col[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], i, col[1]],
                            [i, col[0], row[1], j],
                            [i, j, row[1], col[1]]
                        ]
                    )
                )
        for i in range(row[0] + 1, row[1]):
            for j in range(col[0] + 1, col[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], i, j],
                            [i, col[0], row[1], col[1]],
                            [row[0], j, i, col[1]]
                        ]
                    )
                )
        for i1 in range(row[0] + 1, row[1] - 1):
            for i2 in range(i1 + 1, row[1]):
                ans = min(
                    ans,
                    area(
                        [
                            [row[0], col[0], i1, col[1]],
                            [i1, col[0], i2, col[1]],
                            [i2, col[0], row[1], col[1]]
                        ]
                    )
                )
        return ans
