class Solution {
public:
    int minA(vector<vector<int>>& grid,int left,int right,int top,int down){
        int x2=0;
        int x1=grid.size();
        int y2=0;
        int y1=grid[0].size();
        for(int i=top;i<down;i++){
            for(int j=left;j<right;j++){
                if(grid[i][j]==1){
                    x1=min(x1,i);
                    x2=max(x2,i);
                    y1=min(y1,j);
                    y2=max(y2,j);
                }
            }
        }
        int ans=(x2-x1+1)*(y2-y1+1);
        if(ans<0){
            ans=x1*y1;
        }
        return ans;
    }
    int ansC1(vector<vector<int>>& v){
        int ans=INT_MAX;
        int x=v.size();
        int y=v[0].size();
        for(int i=1;i<x;i++){
            for(int j=1;j<y;j++){

                ans=min(ans,minA(v,0,j,0,i)+minA(v,j,y,0,i)+minA(v,0,y,i,x));
            }
        }
        return ans;
    }
    int ansC2(vector<vector<int>>& v){
        int ans=INT_MAX;
        int x=v.size();
        int y=v[0].size();
        for(int i=1;i<x;i++){
            for(int j=1;j<y;j++){
                ans=min(ans,minA(v,0,y,0,i)+minA(v,0,j,i,x)+minA(v,j,y,i,x));
            }
        }
        // cout<<ans<<endl;
        return ans;
    }
    int ansC3(vector<vector<int>>& v){
        int ans=INT_MAX;
        int x=v.size();
        int y=v[0].size();
        for(int i=1;i<y-1;i++){
            for(int j=i+1;j<y;j++){
                ans=min(ans,minA(v,0,i,0,x)+minA(v,i,j,0,x)+minA(v,j,y,0,x));
            }
        }
        return ans;
    }

