                // cout<<j<<" "<<i<<endl;
                // cout<<minA(v,0,j,0,i)<<minA(v,j,y,0,i)<<minA(v,j,y,0,x)<<endl;
                ans=min(ans,minA(v,0,j,0,i)+minA(v,0,j,i,x)+minA(v,j,y,0,x));
            }
        }
        return ans;
    }

    int ansH3(vector<vector<int>>& v){
        int ans=INT_MAX;
        int x=v.size();
        int y=v[0].size();
        for(int i=1;i<x-1;i++){
            for(int j=i+1;j<x;j++){
                ans=min(ans,minA(v,0,y,0,i)+minA(v,0,y,i,j)+minA(v,0,y,j,x));

            }
        }
        return ans;
    }

