class Solution {
public:
    int minimumArea(vector<vector<int>>& grid, int i1, int i2, int j1, int j2) {
        if (i1 == i2) return 0;
        if (j1 == j2) return 0;
        int n = grid.size();
        int m = grid[0].size();
        int a = INT_MAX;
        int b = INT_MIN;
        int c = INT_MAX;
        int d = INT_MIN;
        int has1 = 0;
        for (int i = i1; i < i2; ++i) {
            for (int j = j1; j < j2; ++j) {
                if (grid[i][j] == 1) {
                    has1 = 1;
                    a = min(a, i);
                    b = max(b, i);
                    c = min(c, j);
                    d = max(d, j);
                }
            }
        }
        return (has1 == 0) ? 1 :((b-a+1) * (d-c+1));
    }

    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int A = 1, B = 1, C = 1;
        int ret = INT_MAX;
        for (int i1 = 0; i1 < n; ++i1) {
            for (int i2 = i1; i2 < n; ++i2) {
                //[0~i1) [i1~i2) [i2~n)
                A = minimumArea(grid, 0, i1, 0, m);
                B = minimumArea(grid, i1, i2, 0, m);
                C = minimumArea(grid, i2, n, 0, m);
                ret = min(ret, A+B+C);
            }
        }
        for (int j1 = 0; j1 < m; ++j1) {
            for (int j2 = j1; j2 < m; ++j2) {
                A = minimumArea(grid, 0, n, 0, j1);
                B = minimumArea(grid, 0, n, j1, j2);
                C = minimumArea(grid, 0, n, j2, m);
                ret = min(ret, A+B+C);
            }
        }
        for (int i1 = 0; i1 < n; ++i1) {
            for (int j1 = 0; j1 < m; ++j1) {
                A = minimumArea(grid, 0, i1, 0, m);
                B = minimumArea(grid, i1, n, 0, j1);
                C = minimumArea(grid, i1, n, j1, m);
                ret = min(ret, A+B+C);
                A = minimumArea(grid, i1, n, 0, m);
                B = minimumArea(grid, 0, i1, 0, j1);
                C = minimumArea(grid, 0, i1, j1, m);
                ret = min(ret, A+B+C);
                A = minimumArea(grid, 0, n, 0, j1);
                B = minimumArea(grid, 0, i1, j1, m);
                C = minimumArea(grid, i1, n, j1, m);
                ret = min(ret, A+B+C);
                A = minimumArea(grid, 0, n, j1, m);
                B = minimumArea(grid, 0, i1, 0, j1);
                C = minimumArea(grid, i1, n, 0, j1);
                ret = min(ret, A+B+C);
            }
        }
        return ret;
    }
};
