class Solution {
    int fun(vector<vector<int>>& a , int x1 , int y1 , int x2 , int y2) {
        int n = a.size(), m = a[0].size();
        int x[2] = {n-1, 0} , y[2] = {m-1 , 0};

        for (int i = x1 ; i <= x2 ; i++) {
            for (int j = y1 ; j <= y2 ; j++) {
                if (!a[i][j]) continue;
                x[0] = min(x[0], i);
                x[1] = max(x[1], i);
                y[0] = min(y[0], j);
                y[1] = max(y[1], j);
            }
        }

        return (x[1] - x[0] + 1) * (y[1] - y[0] + 1);
    }
public:
    int minimumSum(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();

        int ans = n*m;
        for (int i = 0 ; i < n-1 ; i++) {
            for (int j = 0 ; j < m-1 ; j++) {
                ans = min(ans , fun(a,0,0,i,j) + fun(a,0,j+1,i,m-1) + fun(a,i+1,0,n-1,m-1));
                ans = min(ans , fun(a,0,0,i,m-1) + fun(a,i+1,0,n-1,j) + fun(a,i+1,j+1,n-1,m-1));

                ans = min(ans , fun(a,0,0,i,j) + fun(a,i+1,0,n-1,j) + fun(a,0,j+1,n-1,m-1));
                ans = min(ans , fun(a,0,0,n-1,j) + fun(a,0,j+1,i,m-1) + fun(a,i+1,j+1,n-1,m-1));
            }
        }

        for (int i = 0 ; i < n-1 ; i++) {
            for (int j = i+1 ; j < n-1 ; j++) {
                ans = min(ans , fun(a,0,0,i,m-1) + fun(a,i+1,0,j,m-1) + fun(a,j+1,0,n-1,m-1));
            }
        }


        for (int i = 0 ; i < m-1 ; i++) {
            for (int j = i+1 ; j < m-1 ; j++) {
                ans = min(ans , fun(a,0,0,n-1,i) + fun(a,0,i+1,n-1,j) + fun(a,0,j+1,n-1,m-1));
            }
        }

        return ans;
    }
};
