class Solution {
    public int minimumSum(int[][] a) {
        int m = a.length;
        int n = a[0].length;
        int ans = m * n;
        for (int i=1; i<m; i++)
            for (int j=1; j<n; j++) {
                ans = Math.min(ans, c(a, 0, i-1, 0, j-1) + c(a, 0, i-1, j, n-1) + c(a, i, m-1, 0, n-1));
                ans = Math.min(ans, c(a, 0, i-1, 0, j-1) + c(a, i, m-1, 0, j-1) + c(a, 0, m-1, j, n-1));
                ans = Math.min(ans, c(a, 0, i-1, 0, n-1) + c(a, i, m-1, 0, j-1) + c(a, i, m-1, j, n-1));
                ans = Math.min(ans, c(a, 0, m-1, 0, j-1) + c(a, 0, i-1, j, n-1) + c(a, i, m-1, j, n-1));
            }
        for (int i=1; i<m; i++)
            for (int j=i+1; j<m; j++)
                ans = Math.min(ans, c(a, 0, i-1, 0, n-1) + c(a, i, j-1, 0, n-1) + c(a, j, m-1, 0, n-1));
        for (int i=1; i<n; i++)
            for (int j=i+1; j<n; j++)
                ans = Math.min(ans, c(a, 0, m-1, 0, i-1) + c(a, 0, m-1, i, j-1) + c(a, 0, m-1, j, n-1));
        return ans;
    }

    private int c(int[][] grid, int x1, int x2, int y1, int y2) {
        boolean f = true;
        int xx1 = x1;
        int xx2 = x2;
        int yy1 = y1;
        int yy2 = y2;
        for (int i=x1; i<=x2; i++)
            for (int j=y1; j<=y2; j++)
                if (grid[i][j] == 1) {
                    f = false;
                    xx1 = Math.max(xx1, i);
                    xx2 = Math.min(xx2, i);
                    yy1 = Math.max(yy1, j);
                    yy2 = Math.min(yy2, j);
                }
        if (f)
            return 0;
        return (xx1 - xx2 + 1) * (yy1 - yy2 + 1);
    }
}
