class Solution {
    int minimumArea(const vector<vector<int>>& grid, int r0, int c0, int r1, int c1) {
        // cout << r0 << "," << c0 << " -> " << r1 << "," << c1 << endl;
        int l = INT_MAX, r = INT_MIN;
        int t = INT_MAX, b = INT_MIN;
        for(int i = r0; i < r1; i++){
            for(int j = c0; j < c1; j++){
                // cout << i << " " << j << endl;
                if(grid[i][j] == 1){
                    l = min(i, l);
                    r = max(i, r);
                    t = min(j, t);
                    b = max(j, b);
                }
            }
        }
        // cout << l << " " << r << " " << t << " " << b << endl;
        int ans = (l == INT_MAX ? 0 : (r-l+1) * (b-t+1));
        // cout << ans << "*\n";
        return ans;
    }

public:
    int minimumSum(const vector<vector<int>>& grid) {
        const int m = grid.size();
        const int n = grid[0].size();
        int ans = INT_MAX;
        for(int r=1; r<=m; r++){
            for(int c=1; c<=n; c++){
                int split1 = minimumArea(grid, 0, 0, r, c) + minimumArea(grid, 0, c, r, n) + minimumArea(grid, r, 0, m, n);
                int split2 = minimumArea(grid, 0, 0, r, c) + minimumArea(grid, 0, c, m, n) + minimumArea(grid, r, 0, m, c);
                int split3 = minimumArea(grid, r, c, m, n) + minimumArea(grid, 0, 0, r, n) + minimumArea(grid, r, 0, m, c);
                int split4 = minimumArea(grid, r, c, m, n) + minimumArea(grid, 0, c, r, n) + minimumArea(grid, 0, 0, m, c);
                // cout << split1 << " " << split2 << " " << split3 << " " << split4 <<endl;
                ans = min({ans, split1, split2, split3, split4});
            }
        }
        for(int r0=1; r0<m; r0++){
            for(int r1=r0+1; r1<m; r1++){
                int split5 = minimumArea(grid, 0, 0, r0, n) + minimumArea(grid, r0, 0, r1, n) + minimumArea(grid, r1, 0, m, n);
                ans = min(ans, split5);
            }
        }
        for(int c0=1; c0<n; c0++){
            for(int c1=c0+1; c1<n; c1++){
                int split6 = minimumArea(grid, 0, 0, m, c0) + minimumArea(grid, 0, c0, m, c1) + minimumArea(grid, 0, c1, m, n);
                ans = min(ans, split6);
            }
        }
        return ans;
    }
};
