class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto print = [&](vector<vector<int>>grid){
            for(auto it : grid){
                for(auto ti : it)cout<<ti<<" ";
                cout<<endl;
            }
        };
        int n_i = 1e9,m_i = -100;
        int n_j = 1e9,m_j = -100;
        int n = grid.size();
        int m = grid.back().size();
        for(int i = 0;i<n;i++){
            for(int j = 0;j<m;j++){
                if(grid[i][j]){
                    n_i = min(n_i,i);
                    m_i = max(m_i,i);
                    n_j = min(n_j,j);
                    m_j = max(m_j,j);
                }
            }
        }
        vector<vector<int>>mat;
        for(int i = n_i;i<=m_i;i++){
            vector<int>v;
            for(int j = n_j;j<=m_j;j++){
                v.push_back(grid[i][j]);
            }
            mat.push_back(v);
        }
        n = mat.size();
        m = mat.back().size();
        auto outsidex = [&](int x)->int{
            return x<0||x>=n;
        };
        auto outsidey = [&](int y)->int{
            return y<0||y>=m;
        };
        auto area = [&](int fi,int fj,int si,int sj)->int{
            int n_i = 1e9,m_i = -100;
            int n_j = 1e9,m_j = -100;
            if(outsidex(fi)||outsidex(si)||outsidey(fj)||outsidey(sj))return 0;
            for(int i = fi;i<si+1;i++){
                for(int j = fj;j<sj+1;j++){
                    if(mat[i][j]){
                        n_i = min(n_i,i);
                        m_i = max(m_i,i);
                        n_j = min(n_j,j);
                        m_j = max(m_j,j);
                    }
                }
            }
            if(m_i == -100)return 0;
            return (m_i-n_i+1)*(m_j-n_j+1);
        };
        pair<int,int>fst,scd,thrd,frth;
        fst = {0,0};
        scd = {0,m-1};
        thrd = {n-1,0};
        frth = {n-1,m-1};
        int ans = area(0,0,n-1,m-1);
        auto give = [&](int ind,int jnd)->int{
            int a = area(0,0,ind-1,m-1);
            int b = area(ind,0,n-1,jnd);
            int c = area(ind,jnd+1,n-1,m-1);
            int ans = a+b+c;
            {
                a = area(0,0,ind,jnd);
                b = area(0,jnd+1,ind,m-1);
                c = area(ind+1,0,n-1,m-1);
                ans = min(ans,a+b+c);
            }
            {
                a = area(0,0,n-1,jnd-1);
                b = area(0,jnd,ind,m-1);
                c = area(ind+1,jnd,n-1,m-1);
                ans = min(ans,a+b+c);
            }
            {
                a = area(0,0,ind,jnd);
                b =area(ind+1,0,n-1,jnd);
                c = area(0,jnd+1,n-1,m-1);
                ans = min(ans,a+b+c);
            }
            return ans;
        };
        for(int i = 0;i<n;i++){
            for(int j = 0;j<m;j++){
                ans =min(ans,give(i,j));
            }
        }
        for(int i = 0;i<n;i++){
            for(int ii = i;ii<n;ii++){
                int a = area(0,0,i-1,m-1);
                int b = area(i,0,ii,m-1);
                int c = area(ii+1,0,n-1,m-1);
                ans = min(ans,a+b+c);
            }
        }
        for(int i = 0;i<m;i++){
            for(int ii = i;ii<m;ii++){
                int a = area(0,0,n-1,i-1);
                int b = area(0,i,n-1,ii);
                int c = area(0,ii+1,n-1,m-1);
                ans = min(ans,a+b+c);
            }
        }
        return ans;
    }
};
