class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        def minimumArea(sr: int, er: int, sc: int, ec: int) -> int:
            if sr > er or sc > ec:
                return 1000
            high = low = -1
            l = r = -1
            for i in range(sr, er + 1):
                for j in range(sc, ec + 1):
                    x = grid[i][j]
                    if x:
                        if high == -1:
                            high = i
                        low = i
            for j in range(sc, ec + 1):
                for i in range(sr, er + 1):
                    x = grid[i][j]
                    if x:
                        if l == -1:
                            l = j
                        r = j
            return (low - high + 1) * (r - l + 1)

        m, n = len(grid), len(grid[0])
        ans = inf
        # 第一刀横着砍
        for i in range(m - 1):
            # 上半[0, i]
            res1 = minimumArea(sr = 0, er = i, sc = 0, ec = n - 1)
            # 第二刀横着砍
            for i1 in range(i + 1, m - 1):
                res2 = minimumArea(sr = i + 1, er = i1, sc = 0, ec = n - 1) + minimumArea(sr = i1 + 1, er = m - 1, sc = 0, ec = n - 1)
                ans = min(ans, res1 + res2)
            # 第二刀竖着砍
            for j in range(0, n - 1):
                res2 = minimumArea(sr = i + 1, er = m - 1, sc = 0, ec = j) + minimumArea(sr = i + 1, er = m - 1, sc = j + 1, ec = n - 1)
                ans = min(ans, res1 + res2)
            # 下半[i + 1, m - 1]
            res1 = minimumArea(sr = i + 1, er = m - 1, sc = 0, ec = n - 1)
            # 第二刀横着砍
            for i1 in range(i + 1):
                res2 = minimumArea(sr = 0, er = i1, sc = 0, ec = n - 1) + minimumArea(sr = i1 + 1, er = i, sc = 0, ec = n - 1)
                ans = min(ans, res1 + res2)
            # 第二刀竖着砍
            for j in range(0, n - 1):
                res2 = minimumArea(sr = 0, er = i, sc = 0, ec = j) + minimumArea(sr = 0, er = i, sc = j + 1, ec = n - 1)
                ans = min(ans, res1 + res2)

        # 第一刀竖着砍
        for j in range(n - 1):
            # 左半[0, j]
            res1 = minimumArea(sr = 0, er = m - 1, sc = 0, ec = j)
            # 第二刀横着砍
            for i in range(0, m - 1):
                res2 = minimumArea(sr = 0, er = i, sc = j + 1, ec = n - 1) + minimumArea(sr = i + 1, er = m - 1, sc = j + 1, ec = n - 1)
                ans = min(ans, res1 + res2)
            # 第二刀竖着砍j
            for j1 in range(j + 1, n - 1):
                res2 = minimumArea(sr = 0, er = m - 1, sc = j + 1, ec = j1) + minimumArea(sr = 0, er = m - 1, sc = j1 + 1, ec = n - 1)
                ans = min(ans, res1 + res2)
            # 右半[j + 1, n - 1]
            res1 = minimumArea(sr = 0, er = m - 1, sc = j + 1, ec = n - 1)
            # 第二刀横着砍
            for i in range(0, m - 1):
                res2 = minimumArea(sr = 0, er = i, sc = 0, ec = j) + minimumArea(sr = i + 1, er = m - 1, sc = 0, ec = j)
                ans = min(ans, res1 + res2)
            # 第二刀竖着砍j
            for j1 in range(0, j + 1):
                res2 = minimumArea(sr = 0, er = m - 1, sc = 0, ec = j1) + minimumArea(sr = 0, er = m - 1, sc = j1 + 1, ec = j)
                ans = min(ans, res1 + res2)
        return ans
