class Solution {
public:
    int n,m;

    int dfs(int x0,int x1,int y0,int y1,vector<vector<int>>& grid){
        int l0=m,l1=0,r0=n,r1=0;
        for (int i=x0;i<=x1;i++)
            for (int j=y0;j<=y1;j++)
                if (grid[i][j]==1){
                    l0=min(l0,i);
                    l1=max(l1,i);
                    r0=min(r0,j);
                    r1=max(r1,j);
                }
        // cout<<x0<<" * "<<x1<<" * "<<y0<<" * "<<y1<<endl;
        // cout<<l0<<"-"<<l1<<"-"<<r0<<"-"<<r1<<" "<<max(0,(l1-l0+1)*(r1-r0+1))<<endl;
        if (l1<l0 || r1<r0) return 0;
        return (l1-l0+1)*(r1-r0+1);
    }

    int minimumSum(vector<vector<int>>& grid) {
        m=grid.size()-1;
        n=grid[0].size()-1;
        int ans=900,w;
        for (int i=0;i<=m;i++)
            for (int j=0;j<=n;j++){
                w=dfs(0,i,0,j,grid)+dfs(0,m,j+1,n,grid)+dfs(i+1,m,0,j,grid);
                ans=min(ans,w);
                w=dfs(0,i,0,n,grid)+dfs(i+1,m,0,j,grid)+dfs(i+1,m,j+1,n,grid);
                ans=min(ans,w);
                w=dfs(0,i,0,j,grid)+dfs(0,i,j+1,n,grid)+dfs(i+1,m,0,n,grid);
                ans=min(ans,w);
                w=dfs(0,m,0,j,grid)+dfs(0,i,j+1,n,grid)+dfs(i+1,m,j+1,n,grid);
                ans=min(ans,w);
            }
        for (int i=0;i<m;i++)
            for (int j=i+1;j<m;j++){
                w=dfs(0,i,0,n,grid)+dfs(i+1,j,0,n,grid)+dfs(j+1,m,0,n,grid);
                ans=min(ans,w);                
            }
        for (int i=0;i<n;i++)
            for (int j=i+1;j<n;j++){
                w=dfs(0,m,0,i,grid)+dfs(0,m,i+1,j,grid)+dfs(0,m,j+1,n,grid);
                ans=min(ans,w);                
            }
        return ans;
    }
};
