class Solution {
public:
    int minimumArea(vector<vector<int>>& grid,int i_start,int j_start,int i_end,int j_end) {
        int n=grid.size();
        int m=grid.front().size();
        int x1=INT_MAX,y1=INT_MAX,x2=-1,y2=-1;
        bool flag=false;
        for(int i=i_start;i<=i_end;i++){
            for(int j=j_start;j<=j_end;j++){
                if(grid[i][j] == 1){
                    flag=true;
                    x1=min(x1,i);
                    y1=min(y1,j);
                    x2=max(x2,i);
                    y2=max(y2,j);
                }
            }
        }
        if(false == flag) return 0;
        return (x2-x1+1)*(y2-y1+1);
    }

    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid.front().size();
        int dp1[n][m][n][m];
        int dp2[n][m][n][m];

        vector<vector<int>> arr={{1,1},{1,0}};
        // cout<<minimumArea(arr,0,0,0,0)<<endl;
        // cout<<minimumArea(arr,0,0,0,1)<<endl;
        // cout<<minimumArea(arr,0,0,1,0)<<endl;
        // cout<<minimumArea(arr,0,0,1,1)<<endl;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                for(int x=i;x<n;x++){
                    for(int y=j;y<m;y++){
                        dp1[i][j][x][y]=minimumArea(grid,i,j,x,y);
                    }
                }       
            }
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                for(int x=i;x<n;x++){
                    for(int y=j;y<m;y++){
                        int mn=dp1[i][j][x][y];
                        for(int k=j;k<y;k++){
                            int curr=(dp1[i][j][x][k]+dp1[i][k+1][x][y]);
                            mn=min(mn,curr);
                        }
                        for(int l=i;l<x;l++){
                            int curr=(dp1[i][j][l][y]+dp1[l+1][j][x][y]);
                            mn=min(mn,curr);
                        }
                        dp2[i][j][x][y]=mn;
                        // cout<<i<<","<<j<<" => "<<x<<","<<y<<" : "<<mn<<endl;
                    }
                }       
            }
        }
        int ans=dp2[0][0][n-1][m-1];
        for(int x=0;x<n;x++){
            for(int y=0;y<m;y++){

                int mine=dp1[0][0][x][y];

                if(true){
                    int a_1=(x+1<n)?(dp1[x+1][0][n-1][m-1]):0;
                    int b_1=(y+1<m)?(dp1[0][y+1][x][m-1]):0;
                    int a_2=(x+1<n)?(dp2[x+1][0][n-1][m-1]):0;
                    int b_2=(y+1<m)?(dp2[0][y+1][x][m-1]):0;
                    int next=a_1 + b_1;
                    if(a_2 == 0){
                        next=min(next,b_2);
                    }
                    if(b_2 == 0){
                        next=min(next,a_2);
                    }
                    int curr=mine+next;
                    ans=min(ans,curr);
                }
                if(true){
                    int a_1=(x+1<n)?(dp1[x+1][0][n-1][y]):0;
                    int b_1=(y+1<m)?(dp1[0][y+1][n-1][m-1]):0;
                    int a_2=(x+1<n)?(dp2[x+1][0][n-1][y]):0;
                    int b_2=(y+1<m)?(dp2[0][y+1][n-1][m-1]):0;
                    int next=a_1 + b_1;
                    if(a_2 == 0){
                        next=min(next,b_2);
                    }
                    if(b_2 == 0){
                        next=min(next,a_2);
                    }
                    int curr=mine+next;
                    ans=min(ans,curr);
                }
            }
        }

        return ans;

    }
};
