class Solution {
public:
    int INF = 1e9;

    int check(int up, int down, int left, int right, vector<vector<int>>& grid){
        int n=grid.size(), m=grid[0].size();
        if(down>=n || right>=m) return 0;
        int u=down, d=up, l=right, r=left;
        for(int i=up;i<=down;i++){
            for(int j=left;j<=right;j++){
                if(grid[i][j]==1){
                    u=min(u, i);
                    l=min(l, j);
                    d=max(d, i);
                    r=max(r, j);
                }
            }
        }
        return max(0, (d-u+1)*(r-l+1));
    }

    int minimumSum(vector<vector<int>>& grid) {
        int n=grid.size(), m=grid[0].size(), ans=INF;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int v1, v2, v3;
                v1 = check(0,i,0,j,grid);
                v2 = check(0,i,j+1,m-1,grid);
                v3 = check(i+1,n-1,0,m-1,grid);
                ans=min(ans, v1+v2+v3);

                v1 = check(0,i,0,m-1,grid);
                v2 = check(i+1,n-1,0,j,grid);
                v3 = check(i+1,n-1,j+1,m-1,grid);
                ans=min(ans, v1+v2+v3);

                v1 = check(0,n-1,0,j,grid);
                v2 = check(0,i,j+1,m-1,grid);
                v3 = check(i+1,n-1,j+1,m-1,grid);
                ans=min(ans, v1+v2+v3);

                v1 = check(0,i,0,j,grid);
                v2 = check(i+1,n-1,0,j,grid);
                v3 = check(0,n-1,j+1,m-1,grid);
                ans=min(ans, v1+v2+v3);
            }
        }

        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                int v1 = check(0,i,0,m-1,grid);
                int v2 = check(i+1,j,0,m-1,grid);
                int v3 = check(j+1,n-1,0,m-1,grid);
                ans=min(ans, v1+v2+v3);
            }
        }

        for(int i=0;i<m;i++){
            for(int j=i+1;j<m;j++){
                int v1 = check(0,n-1,0,i,grid);
                int v2 = check(0,n-1,i+1,j,grid);
                int v3 = check(0,n-1,j+1,m-1,grid);
                ans=min(ans, v1+v2+v3);
            }
        }

        return ans;
    }
};
