class Solution {
    int calc(vector<vector<int>>& grid,int m1,int m2,int n1,int n2) {
        //cout<<m1<<" "<<m2<<" "<<n1<<" "<<n2<<endl;
        int r1=m2,r2=m1,c1=n2,c2=n1,m=grid.size(),n=grid[0].size();
        bool f=false;
        for(int i=m1;i<=m2;i++){
            for(int j=n1;j<=n2;j++){
                if(grid[i][j]){
                    r1=min(i,r1);
                    r2=max(i,r2);
                    c1=min(c1,j);
                    c2=max(j,c2);
                    f=true;
                }
            }
        }
        if(!f)
            return m*n;
        //cout<<(r2-r1+1)*(c2-c1+1)<<endl;;
        //cout<<r1<<" "<<r2<<" "<<c1<<" "<<c2<<endl;
        return (r2-r1+1)*(c2-c1+1);
    }
public:
    int minimumSum(vector<vector<int>>& grid) {
        int m=grid.size(),n=grid[0].size();
        int ans=m*n;
        for(int i=0;i<m;i++){
            for(int j=0;j<n-1;j++){
                int cur1=calc(grid,0,i,0,j)+calc(grid,0,i,j+1,n-1)+calc(grid,i+1,m-1,0,n-1);
                ans=min(ans,cur1);
                int cur2=calc(grid,0,i,0,n-1)+calc(grid,i+1,m-1,j+1,n-1)+calc(grid,i+1,m-1,0,j);
                ans=min(ans,cur2);
                int cur3=calc(grid,0,i,0,j)+calc(grid,0,m-1,j+1,n-1)+calc(grid,i+1,m-1,0,j);
                ans=min(ans,cur3);
            }
            for(int j=1;j<m-1;j++){
                int cur=calc(grid,0,i,0,n-1)+calc(grid,i+1,j,0,n-1)+calc(grid,j+1,m-1,0,n-1);
                ans=min(ans,cur);
            }
        }
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n-1;j++){
                int cur=calc(grid,0,m-1,0,i)+calc(grid,0,m-1,i+1,j)+calc(grid,0,m-1,j+1,n-1);
                ans=min(ans,cur);
            }
            for(int j=0;j<m-1;j++){
                int cur=calc(grid,0,m-1,0,i)+calc(grid,0,j,i+1,n-1)+calc(grid,j+1,m-1,i+1,n-1);
                ans=min(ans,cur);
                //cout<<endl;
            }
        }
        return ans;
    }
};
