class Solution {
public:
    int minimumArea(vector<vector<int>>& grid, int x, int y, int x2, int y2) {
        int m = grid.size(), n = grid[0].size();
        if (x2 < 0 || y2 < 0 || y < 0 || x < 0 || x >= m || y >= n || x2 >= m || y2 >= n) return 0;
        if (x > x2 || y > y2) return 0;
        int mix = 1e9, miy = 1e9, mx = 0, may = 0;
        for (int i = x; i <= x2; i++) {
            for (int j = y; j <= y2; j++) {
                if (grid[i][j]) {
                    mix = min(mix, i);
                    miy = min(miy, j);
                    mx = max(mx, i);
                    may = max(may, j);
                }
            }
        }
        if (mix == 1e9) return 0;
        //cout << x << " " << y << " " << x2 << " " << y2 << endl;
        return (mx - mix + 1) * (may - miy + 1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        int res = 1e9;
        int m = grid.size(), n = grid[0].size();
        //枚举中心点
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                int x1 = minimumArea(grid, 0, 0, i, j);
                int x2 = minimumArea(grid, 0, j + 1, i, n - 1);
                int x3 = minimumArea(grid, i + 1, 0, m - 1, n - 1);
                res = min(res, x1 + x2 + x3);
                x1 = minimumArea(grid, 0, 0, i - 1, n - 1);
                x2 = minimumArea(grid, i, 0, m - 1, j);
                x3 = minimumArea(grid, i, j + 1, m -1, n - 1);
                res = min(res, x1 + x2 + x3);
                x1 = minimumArea(grid, 0, 0, i, j);
                x2 = minimumArea(grid, i + 1, 0, m - 1, j);
                x3 = minimumArea(grid, 0, j + 1, m - 1, n - 1);
                res = min(res, x1 + x2 + x3);
                x1 = minimumArea(grid, 0, j, i, n - 1);
                x2 = minimumArea(grid, i + 1, j, m - 1, n - 1);
                x3 = minimumArea(grid, 0, 0, m - 1, j - 1);
                res = min(res, x1 + x2 + x3); 
            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = i + 1; j < m; j++) {
                int x1 = minimumArea(grid, 0, 0, i, n - 1);
                int x2 = minimumArea(grid, i + 1, 0, j, n - 1);
                int x3 = minimumArea(grid, j + 1, 0, m - 1, n - 1);
                res = min(res, x1 + x2 + x3);
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                int x1 = minimumArea(grid, 0, 0, m - 1, i);
                int x2 = minimumArea(grid, 0, i + 1, m - 1, j);
                int x3 = minimumArea(grid, 0, j + 1, m - 1, n - 1);
                res = min(res, x1 + x2 + x3);
            }
        }
        return res;
    }
};
