    ll count(vt<vt<int>>&g, int sti , int stj , int endi , int endj){
        int n = g.size() , m = g[0].size();
        int maxi = -1, maxj = -1, minj = m , mini = n;
        rep(i , sti, endi){
            rep(j , stj, endj){
                if(g[i][j]){
                    maxi = max(maxi , i);
                    maxj = max(maxj , j);
                    mini = min(mini , i);
                    minj = min(minj , j);
                }
            }
        }
        if(maxi == -1 ){return 0;}
        return max(0 , (abs(maxi-mini+1) * abs(maxj - minj+1)));
    }
