class Solution {
public:
    int minimumSum(vector<vector<int>>& g) {
        int n = g.size(), m = g[0].size();
        int ans = 1e9;
        auto get = [&](int x1, int y1, int x2, int y2) {
            int l = 1e9, r = 0, d = 0, u = 1e9;
            for (int i = x1; i <= x2; i++) {
                for (int j = y1; j <= y2; j++) {
                    if (g[i][j] == 1) {
                        l = std::min(l, i);
                        r = std::max(r, i);
                        d = std::max(d, j);
                        u = std::min(u, j);
                    }
                }
            }
            return std::max(0, r - l + 1) * std::max(0, d - u + 1);
        };
        for (int i = 0; i < n - 1; i++) {
            for (int j = 0; j < m - 1; j++) {
                ans = std::min(ans, get(0, 0, i, m - 1) + get(i + 1, 0, n - 1, j) + get(i + 1, j + 1, n - 1, m - 1));
                ans = std::min(ans, get(0, 0, i, j) + get(0, j + 1, i, m - 1) + get(i + 1, 0, n - 1, m - 1));
            }
        }
        for (int i = 0; i < m - 1; i++) {
            for (int j = 0; j < n - 1; j++) {
                ans = std::min(ans, get(0, 0, j, i) + get(j + 1, 0, n - 1, i) + get(0, i + 1, n - 1, m - 1));
                ans = std::min(ans, get(0, 0, n - 1, i) + get(0, i + 1, j, m - 1) + get(j + 1, i + 1, n - 1, m - 1));
            }
        }

        for (int i = 0; i + 2 < n; i++) {
            for (int j = i + 1; j + 1 < n; j++) {
                ans = std::min(ans, get(0, 0, i, m - 1) + get(i + 1, 0, j, m - 1) + get(j + 1, 0, n - 1, m - 1));
            }
        }

        for (int i = 0; i + 2 < m; i++) {
            for (int j = i + 1; j + 1 < m; j++) {
                ans = std::min(ans, get(0, 0, n - 1, i) + get(0, i + 1, n - 1, j) + get(0, j + 1, n - 1, m - 1));
            }
        }

        return ans;
    }
};
