class Solution {
public:
    vector<vector<int>> g;
    int w1(int x1,int y1,int x2,int y2) {
        int l1=1000,r1=-1,l2=1000,r2=-1;
        for(int i=x1;i<=x2;i++){
            for(int j=y1;j<=y2;j++){
                if(g[i][j]){
                    l1=min(l1,i);
                    r1=max(r1,i);
                    l2=min(l2,j);
                    r2=max(r2,j);
                }
            }
        }
        return (r2-l2+1)*(r1-l1+1);
    }
    int dfs(int x1,int y1,int x2,int y2,int w){
        if(w==1){
            return w1(x1,y1,x2,y2);
        }
        else if(w==3){
            int s=0x3f3f3f3f;
            for(int i=x1;i<x2;i++){
                s=min({s,dfs(x1,y1,i,y2,1)+dfs(i+1,y1,x2,y2,2),dfs(x1,y1,i,y2,2)+dfs(i+1,y1,x2,y2,1)});
            }
            for(int i=y1;i<y2;i++){
                s=min({s,dfs(x1,y1,x2,i,1)+dfs(x1,i+1,x2,y2,2),dfs(x1,y1,x2,i,2)+dfs(x1,i+1,x2,y2,1)});
            }
            return s;
        }
        int s=0x3f3f3f3f;
            for(int i=x1;i<x2;i++){
                s=min({s,dfs(x1,y1,i,y2,1)+dfs(i+1,y1,x2,y2,1),dfs(x1,y1,i,y2,1)+dfs(i+1,y1,x2,y2,1)});
            }
            for(int i=y1;i<y2;i++){
                s=min({s,dfs(x1,y1,x2,i,1)+dfs(x1,i+1,x2,y2,1),dfs(x1,y1,x2,i,1)+dfs(x1,i+1,x2,y2,1)});
            }
            return s;
    }
    int minimumSum(vector<vector<int>>& grid) {
        g=grid;
        return dfs(0,0,g.size()-1,g[0].size()-1,3);
    }
};
