class Solution {
    TreeSet<Integer>[] ts;
    public int get(int x0, int y0, int x1, int y1) {
        return (x1 - x0 + 1) * (y1 - y0 + 1);
    }
    public int get1(int x0, int y0, int x1, int y1) {
        int ax0 = x1, ay0 = y1, ax1 = x0, ay1 = y0;
        boolean chk = false;
        for(int i = x0; i <= x1; i ++) {
            if(ts[i].ceiling(y0) != null && ts[i].ceiling(y0) <= y1) {
                ax0 = Math.min(ax0, i);
                ax1 = Math.max(ax1, i);
                ay0 = Math.min(ay0, ts[i].ceiling(y0));
                ay1 = Math.max(ay1, ts[i].floor(y1));
                chk = true;
            }
        }
        if(!chk) {
            return 0;
        }
        return get(ax0, ay0, ax1, ay1);
    }
    public int get2(int x0, int y0, int x1, int y1) {
        int min = get1(x0, y0, x1, y1);
        for(int i = x0; i < x1; i ++) {
            min = Math.min(min, get1(x0, y0, i, y1) + get1(i + 1, y0, x1, y1));
        }
        for(int i = y0; i < y1; i ++) {
            min = Math.min(min, get1(x0, y0, x1, i) + get1(x0, i + 1, x1, y1));
        }
        return min;
    }
    public int get3(int x0, int y0, int x1, int y1) {
        int min = get2(x0, y0, x1, y1);
        for(int i = x0; i < x1; i ++) {
            min = Math.min(min, get1(x0, y0, i, y1) + get2(i + 1, y0, x1, y1));
            min = Math.min(min, get2(x0, y0, i, y1) + get1(i + 1, y0, x1, y1));
        }
        for(int i = y0; i < y1; i ++) {
            min = Math.min(min, get1(x0, y0, x1, i) + get2(x0, i + 1, x1, y1));
            min = Math.min(min, get2(x0, y0, x1, i) + get1(x0, i + 1, x1, y1));
        }
        return min;
    }
    public int minimumSum(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        ts = new TreeSet[m];
        int x0 = m, y0 = n, x1 = 0, y1 = 0;
        for(int i = 0; i < m; i ++) {
            ts[i] = new TreeSet();
            for(int j = 0; j < n; j ++) {
                if(grid[i][j] == 1) {
                    ts[i].add(j);
                    x0 = Math.min(x0, i);
                    y0 = Math.min(y0, j);
                    x1 = Math.max(x1, i);
                    y1 = Math.max(y1, j);
                }
            }
        }
        return get3(x0, y0, x1, y1);
    }
}
