class Solution {
public:
    bool c(int x1, int y1, int x2, int y2, int xx1, int yy1, int xx2, int yy2){
        int ll=max(y1,yy1);
        int rr=min(y2,yy2);
        if(ll>rr)
            return 0;
        int uu=max(x1,xx1);
        int dd=min(x2,xx2);
        if(uu>dd)
            return 0;
        return 1;
    }
    int minimumSum(vector<vector<int>>& grid) {
        int m=grid.size();
        int n=grid[0].size();
        int l=INT_MAX,r=0;
        int u=INT_MAX,d=0;
        for(int i=0;i<m;++i){
            for(int j=0;j<n;++j){
                if(grid[i][j]){
                    l=min(l,j);
                    r=max(r,j);
                    u=min(u,i);
                    d=max(d,i);
                }
            }
        }
        int mi=INT_MAX;
        for(int i=0;i<m;++i){
            if(grid[i][l]){
                mi=i;
                break;
            }
        }
        int res=(r-l+1)*(d-u+1);
        // cout<<"res="<<res<<endl;
        for(int iu=u;iu<=mi;++iu){
            for(int id=iu;id<=d;++id){
                for(int jr=l;jr<=r;++jr){
                    int t=(jr-l+1)*(id-iu+1);
                    if(t>=res)
                        continue;
                    int ll=INT_MAX,rr=0;
                    int uu=INT_MAX,dd=0;
                    vector<int> s;
                    int k=0;
                    for(int i=u;i<=d;++i){
                        for(int j=l;j<=r;++j){
                            if(grid[i][j]&&!(i>=iu&&i<=id&&j>=l&&j<=jr)){
                                ++k;
                                ll=min(ll,j);
                                rr=max(rr,j);
                                uu=min(uu,i);
                                dd=max(dd,i);
                                // cout<<"i="<<i<<" j="<<j<<endl;
                            }
                        }
                        if(k&&!c(iu,l,id,jr,uu,ll,dd,rr)){
                            s.push_back((rr-ll+1)*(dd-uu+1));
                        }
                        else{
                            s.push_back(900);
                        }
                    }
                    ll=INT_MAX,rr=0;
                    uu=INT_MAX,dd=0;
                    k=0;
                    for(int i=d;i>u;--i){
                        for(int j=l;j<=r;++j){
                            if(grid[i][j]&&!(i>=iu&&i<=id&&j>=l&&j<=jr)){
                                ++k;
                                ll=min(ll,j);
                                rr=max(rr,j);
                                uu=min(uu,i);
                                dd=max(dd,i);
                                // cout<<"i="<<i<<" j="<<j<<endl;
                            }
                        }
                        if(k&&!c(iu,l,id,jr,uu,ll,dd,rr)){
                            int ts=(rr-ll+1)*(dd-uu+1);
                            res=min(res,t+ts+s[i-u-1]);
                        }
                    }

                    ll=INT_MAX,rr=0;
                    uu=INT_MAX,dd=0;
                    k=0;
                    s.resize(0);
                    for(int j=l;j<=r;++j){
                        for(int i=u;i<=d;++i){
                            if(grid[i][j]&&!(i>=iu&&i<=id&&j>=l&&j<=jr)){
                                ++k;
                                ll=min(ll,j);
                                rr=max(rr,j);
                                uu=min(uu,i);
                                dd=max(dd,i);
                                // cout<<"i="<<i<<" j="<<j<<endl;
                            }
                        }
                        if(k&&!c(iu,l,id,jr,uu,ll,dd,rr)){
                            s.push_back((rr-ll+1)*(dd-uu+1));
                        }
                        else{
                            s.push_back(900);
                        }
                    }
                    ll=INT_MAX,rr=0;
                    uu=INT_MAX,dd=0;
                    k=0;
                    for(int j=r;j>l;--j){
                        for(int i=u;i<=d;++i){
                            if(grid[i][j]&&!(i>=iu&&i<=id&&j>=l&&j<=jr)){
                                ++k;
                                ll=min(ll,j);
                                rr=max(rr,j);
                                uu=min(uu,i);
                                dd=max(dd,i);
                                // cout<<"i="<<i<<" j="<<j<<endl;
                            }
                        }
                        if(k&&!c(iu,l,id,jr,uu,ll,dd,rr)){
                            int ts=(rr-ll+1)*(dd-uu+1);
                            res=min(res,t+ts+s[j-l-1]);
                        }
                    }



                }
            }
        }
        return res;
    }
};
