class Solution {
    int f(vector<vector<int>>& grid, int xl, int xr, int yt, int yb) {
      int l=1000,r=0,t=1000,b=0;

      int n = grid.size();
      int m = grid[0].size();
      if (xl >= n || xr > n || yt  >= m || yb > m) return 1000;

      for (int i = xl; i < xr; i++) {
        for (int j = yt; j < yb; j++) {
          if (grid[i][j] == 1) {
            t = min(t, i);
            b = max(b, i);
            l = min(l, j);
            r = max(r, j);
          }
        }
      }
      if (l == 1000) return 0;
      return (r-l+1)*(b-t+1);
    }
public:
    int minimumSum(vector<vector<int>>& grid) {
      int ans = 1e9;
      int n = grid.size();
      int m = grid[0].size();
      for (int i = 0; i < n; i++) {
        for (int j = i+1; j < n; j++) {
          int a = f(grid, 0, i, 0, m);
          int b = f(grid, i, j, 0, m);
          int c = f(grid, j, n, 0, m);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
     for (int i = 0; i < m; i++) {
        for (int j = i+1; j < m; j++) {
          int a = f(grid, 0, n, 0, i);
          int b = f(grid, 0, n, i, j);
          int c = f(grid, 0, n, j, m);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
//       2
     for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
          int a = f(grid, 0, i, 0, m);
          int b = f(grid, i, n, 0, j);
          int c = f(grid, i, n, j, m);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
     for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
          int a = f(grid, n-i-1, n, 0, m);
          int b = f(grid, 0, n-i-1, 0, j);
          int c = f(grid, 0, n-i-1, j, m);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
//       1
     for (int i = 0; i < m; i++) {
        for (int j = 0; j < n; j++) {
          int a = f(grid, 0, n, 0, i);
          int b = f(grid, 0, j, i, m);
          int c = f(grid, j, n, i, m);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
     for (int i = 0; i < m; i++) {
        for (int j = 0; j < n; j++) {
          int a = f(grid, 0, n, m-i-1, m);
          int b = f(grid, 0, j, 0, m-i-1);
          int c = f(grid, j, n, 0, m-i-1);
          if (!a || !b || !c) continue;
          ans = min(ans, a + b + c);
        }
      }
      return ans;

    }
};
