class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int ans = INT_MAX, m = grid.size(), n = grid[0].size();
        auto cal = [&](int a, int b, int c, int d){
            int lh = 50, rh = -1, lw = 50, rw = -1;
            for(int i=a;i<=c;i++){
                for(int j=b;j<=d;j++){
                    if(grid[i][j]) {
                        lh = min(i, lh);
                        rh = max(i, rh);
                        lw = min(j, lw);
                        rw = max(j, rw);
                    }
                }
            }
            if(rh >= lh)
                return (rh - lh + 1) * (rw - lw + 1);
            return 1;
        };
        for(int i=1;i<m;i++){
            int up = cal(0, 0, i-1, n-1), down = cal(i, 0, m-1, n-1);
            // down
            int a = i, b = 0, c = m - 1, d = n - 1;
            for(int j=a+1;j<=c;j++){
                int x = cal(a, b, j-1, d), y = cal(j, b, c, d);
                ans = min(ans, up + x + y);
            }
            for(int j=b+1;j<=d;j++){
                int x = cal(a, b, c, j-1), y = cal(a, j, c, d);
                ans = min(ans, up + x + y);
            }
            // up
            a = 0, b = 0, c = i-1, d = n - 1;
            for(int j=a+1;j<=c;j++){
                int x = cal(a, b, j-1, d), y = cal(j, b, c, d);
                ans = min(ans, down + x + y);
            }
            for(int j=b+1;j<=d;j++){
                int x = cal(a, b, c, j-1), y = cal(a, j, c, d);
                ans = min(ans, down + x + y);
            }
        }
        for(int i=1;i<n;i++){
            int l = cal(0, 0, m-1, i-1), r = cal(0, i, m-1, n-1);
            // left
            int a = 0, b = 0, c = m - 1, d = i - 1;
            for(int j=a+1;j<=c;j++){
                int x = cal(a, b, j-1, d), y = cal(j, b, c, d);
                ans = min(ans, r + x + y);
            }
            for(int j=b+1;j<=d;j++){
                int x = cal(a, b, c, j-1), y = cal(a, j, c, d);
                ans = min(ans, r + x + y);
            }
            // right
            a = 0, b = i, c = m - 1, d = n - 1;
            for(int j=a+1;j<=c;j++){
                int x = cal(a, b, j-1, d), y = cal(j, b, c, d);
                ans = min(ans, l + x + y);
            }
            for(int j=b+1;j<=d;j++){
                int x = cal(a, b, c, j-1), y = cal(a, j, c, d);
                ans = min(ans, l + x + y);
            }
        }
        return ans;
    }
};
