class Solution {
public:
    int solve(int stx,int edx,int sty,int edy,vector<vector<int>>&grid){
        int x1 = edx,y1 = edy;
        int x2 = stx,y2 = sty;
        for(int i = stx;i<=edx;++i){
            for(int j = sty;j<=edy;++j){
                if(grid[i][j] == 0)continue;
                x1 = min(x1,i);
                y1 = min(y1,j);

                x2 = max(x2,i);
                y2 = max(y2,j);
            }
        }
        if(x2<x1)return 0;
        return max(0,(x2 - x1 + 1) * (y2 - y1 + 1));
    }
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(),m = grid[0].size();
        int res = n * m;
        for(int i = 0;i<n;++i){
            for(int j = i;j<n;++j){
                res = min(res,
                solve(0,i,0,m-1,grid) + solve(i+1,j,0,m-1,grid) + solve(j+1,n-1,0,m-1,grid));
            }
        }
        for(int i = 0;i<m;++i){
            for(int j = i;j<m;++j){
                res = min(res,
                solve(0,n-1,0,i,grid) + solve(0,n-1,i+1,j,grid) + solve(0,n-1,j+1,m-1,grid));
            }
        }
        for(int i = 0;i<n;++i){
            int ac0 = solve(0,i,0,m-1,grid);
            for(int j = 0;j<m;++j){
                res = min(res,ac0 + solve(i+1,n-1,0,j,grid) + solve(i+1,n-1,j+1,m-1,grid));
            }
            int ac1 = solve(i+1,n-1,0,m-1,grid);
            for(int j = 0;j<m;++j){
                res = min(res,ac1 + solve(0,i,0,j,grid) + solve(0,i,j+1,m-1,grid));
            }
        }
        for(int i = 0;i<m;++i){
            int ac0 = solve(0,n-1,0,i,grid);
            for(int j = 0;j<n;++j){
                res = min(res,ac0 + solve(0,j,i+1,m-1,grid) + solve(j+1,n-1,i+1,m-1,grid));
            }
            int ac1 = solve(0,n-1,i+1,m-1,grid);
            for(int j = 0;j<n;++j){
                res = min(res,ac1 + solve(0,j,0,i,grid) + solve(j+1,n-1,0,i,grid));
            }
        }
        return res;
    }
};
