class Solution {
     int minimumArea(vector<vector<int>>& grid, int m1, int m, int n1, int n) {
        //int m = grid.size();
        //int n = grid[0].size();
        int left = INT_MAX;
        int right = INT_MIN;
        int top = INT_MAX;
        int bottom = INT_MIN;
        for (int i = m1; i < m; i++) {
            for (int j = n1; j < n; j++) {
                if (grid[i][j] == 1) {
                    left = min(left, j);
                    right = max(right, j);
                    top = min(top, i);
                    bottom = max(bottom, i);
                }
            }
        }
        if (left == INT_MAX) return 0;
        return (right-left+1)*(bottom-top+1);

    }

public:
    int minimumSum(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ret = INT_MAX;
        for (int i1 = 0; i1 < m; i1++) {
            for (int i2 = i1; i2 < m; i2++) {
                int now = 0;
                now += minimumArea(grid, 0, i1, 0, n);
                now += minimumArea(grid, i1, i2, 0, n);
                now += minimumArea(grid, i2, m, 0, n);
                ret = min(ret, now);
            }
        }
        for (int j1 = 0; j1 < n; j1++) {
            for (int j2 = j1; j2 < n; j2++) {
                int now = 0;
                now += minimumArea(grid, 0, m, 0, j1);
                now += minimumArea(grid, 0, m, j1, j2);
                now += minimumArea(grid, 0, m, j2, n);
                ret = min(ret, now);
            }
        }
        for (int i1 = 0; i1 < m; i1++) {
            for (int j1 = 0; j1 < n; j1++) {
                int now = 0;
                now += minimumArea(grid, 0, i1, 0, n);
                now += minimumArea(grid, i1, m, 0, j1);
                now += minimumArea(grid, i1, m, j1, n);
                ret = min(ret, now);
                now = 0;
                now += minimumArea(grid, 0, i1, 0, j1);
                now += minimumArea(grid, 0, i1, j1, n);
                now += minimumArea(grid, i1, m, 0, n);
                ret = min(ret, now);
                now = 0;
                now += minimumArea(grid, 0, m, 0, j1);
                now += minimumArea(grid, 0, i1, j1, n);
                now += minimumArea(grid, i1, m, j1, n);
                ret = min(ret, now);
                now = 0;
                now += minimumArea(grid, 0, i1, 0, j1);
                now += minimumArea(grid, i1, m, 0, j1);
                now += minimumArea(grid, 0, m, j1, n);
                ret = min(ret, now);
            }
        }
        return ret;

    }
};
