class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])

        @cache
        def dfs(row_left,row_right,col_left,col_right):
            min_row = inf
            min_h = inf
            max_row = - inf
            max_h = - inf
            for i in range(row_left,row_right+1):
                for j in range(col_left,col_right+1):
                    if grid[i][j] == 1:
                        min_row = min(j,min_row)
                        max_row = max(j,max_row)
                        min_h = min(i,min_h)
                        max_h = max(i,max_h)
            if min_row == inf:
                return 0 
            return (max_h-min_h+1) * (max_row-min_row+1)

        ans = inf
        for i in range(m-2):
            for j in range(i+1,m-1):
                a = dfs(0,i,0,n-1)
                b = dfs(i+1,j,0,n-1)
                c = dfs(j+1,m-1,0,n-1)
                ans = min(a+b+c,ans)
        for i in range(m-1):
            for j in range(n-1):
                a = dfs(0,i,0,n-1)
                b = dfs(i+1,m-1,0,j)
                c = dfs(i+1,m-1,j+1,n-1)
                ans = min(a+b+c,ans)
        for i in range(n-2):
            for j in range(i+1,n-1):
                a = dfs(0,m-1,0,i)
                b = dfs(0,m-1,i+1,j)
                c = dfs(0,m-1,j+1,n-1)
                ans = min(a+b+c,ans)
        for i in range(n-1):
            for j in range(m-1):
                a = dfs(0,m-1,0,i)
                b = dfs(0,j,i+1,n-1)
                c = dfs(j+1,m-1,i+1,n-1)
                ans = min(a+b+c,ans)
        for i in range(0,m-1):
            for j in range(0,n-1):
                a = dfs(0,i,0,j)
                b1 = dfs(0,i,j+1,n-1)
                b2 = dfs(0,m-1,j+1,n-1)
                c1 = dfs(i+1,m-1,0,j)
                c2 = dfs(i+1,m-1,0,n-1)
                ans = min(a+b1+c2,a+b2+c1,ans)
        return ans
