class Solution {
public:
    int f(vector<vector<int>>& arr) {
        int n= arr.size(), m=arr[0].size();
        int xl=n, xr=0;
        int yl=m, yr=0;

        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                if(arr[i][j]){
                    xl= min(xl, i);
                    xr= max(xr, i);
                    yl= min(yl, j);
                    yr= max(yr, j);
                }
            }
        }
        return (xr-xl+1)*(yr-yl+1);
    }



    int minimumSum(vector<vector<int>>& arr) {
        int n= arr.size(), m=arr[0].size();

        auto fill= [&](int x1, int y1, int x2, int y2)-> vector<vector<int>>{
            int r= x2-x1+1, c=y2-y1+1;
            vector<vector<int>> ans(r, vector<int>(c, 0));
            for(int i=x1; i<=x2; i++){
                for(int j=y1; j<=y2; j++){
                    ans[i-x1][j-y1]= arr[i][j];
                }
            }
            return ans;
        };
        int ans= n*m;
        for(int i=0; i<n; i++){
            for(int j=0; j<m; j++){
                //column wise
                if(i<n-1 && j<m-1){
                    vector<vector<int>>arr1= fill(0, 0, i, j);
                    vector<vector<int>>arr2= fill(i+1, 0, n-1, j);
                    vector<vector<int>>arr3= fill(0, j+1, n-1, m-1);
                    ans= min(ans, f(arr1)+f(arr2)+f(arr3));
                }

                if(i<n-1 && j<m-1){
                    vector<vector<int>>arr11= fill(0, 0, i, j);
                    vector<vector<int>>arr22= fill(i+1, 0, n-1, m-1);
                    vector<vector<int>>arr33= fill(0, j+1, i, m-1);
                    ans= min(ans, f(arr11)+f(arr22)+f(arr33));
                }

                if(i<n-1 && j<m-1){
                    vector<vector<int>>arr111= fill(i+1, j+1, n-1, m-1);
                    vector<vector<int>>arr222= fill(0, 0, n-1, j);
                    vector<vector<int>>arr333= fill(0, j+1, i, m-1);
                    ans= min(ans, f(arr111)+f(arr222)+f(arr333));
                }

                if(i<n-1 && j<m-1){
                    vector<vector<int>>arr1111= fill(i+1, j+1, n-1, m-1);
                    vector<vector<int>>arr2222= fill(0, 0, i, m-1);
                    vector<vector<int>>arr3333= fill(i+1, 0, n-1, j);
                    ans= min(ans, f(arr1111)+f(arr2222)+f(arr3333));
                }
            }
        }

        for(int i1=0; i1<n; i1++){
            for(int i2=i1+1; i2<n; i2++){
                if(i2<n-1){
                    vector<vector<int>>arr1= fill(0, 0, i1, m-1);
                    vector<vector<int>>arr2= fill(i1+1, 0, i2, m-1);
                    vector<vector<int>>arr3= fill(i2+1, 0, n-1, m-1);
                    ans= min(ans, f(arr1)+f(arr2)+f(arr3));
                }
            }
        }

        for(int i1=0; i1<m; i1++){
            for(int i2=i1+1; i2<m; i2++){
                if(i2<m-1){
                    vector<vector<int>>arr1= fill(0, 0, n-1, i1);
                    vector<vector<int>>arr2= fill(0, i1+1, n-1, i2);
                    vector<vector<int>>arr3= fill(0, i2+1, n-1, m-1);
                    ans= min(ans, f(arr1)+f(arr2)+f(arr3));
                }
            }
        }
        return ans;
    }
};
