class Solution {
    int minimumArea(vector<vector<int>>& g, int p, int q, int u, int v) {
        int a = INT_MAX, b = INT_MIN, c = INT_MAX, d = INT_MIN;
        if(q < p || v < u) {
            return 0;
        }
        for(int i = p; i <= q; i ++) {
            for(int j = u; j <= v; j ++) {
                if(g[i][j]) {
                    if(a > i) {
                        a = i;
                    }
                    if(b < i) {
                        b = i;
                    }
                    if(c > j) {
                        c = j;
                    }
                    if(d < j) {
                        d = j;
                    }
                }
            }
        }
        if(a > b) {
            return 0;
        }
        return (b - a + 1) * (d - c + 1);
    }
