    private int[] minimumArea(int[][] grid, int x0, int x1, int y0, int y1) {
        int[] ans = new int[2];
        int xMin = 30;
        int xMax = 0;
        int yMin = 30;
        int yMax = 0;
        for(int i = x0; i <= x1; i++){
            for(int j = y0; j <= y1; j++) {
                if(grid[i][j] == 1) {
                    ans[0] = 1;
                    xMin = Math.min(i, xMin);
                    xMax = Math.max(i, xMax);
                    yMin = Math.min(j, yMin);
                    yMax = Math.max(j, yMax);
                     //System.out.println("i: " + i + " j: " + j);
                }
            }   
        }
        ans[1] = (xMax-xMin + 1) *  (yMax-yMin + 1);
        return ans;
    }
