class Solution {
    public int minimumSum(int[][] grid) {
        int n = grid.length;
        int m = grid[0].length;

        int ans  = 100000;

        for(int i = 0;i < n;i ++)
            for(int j = 0;j < m;j ++){
                //第一种情况;
                int Ans = solve(grid,0,0,n - 1,j);
                Ans += solve(grid,0,j + 1,i,m - 1);
                Ans += solve(grid,i + 1,j + 1,n - 1,m - 1);

                ans = Math.min(ans,Ans);
            }


        for(int i = 0;i < n;i ++)
            for(int j = 0;j < m;j ++){
                //第二种情况;
                int Ans = solve(grid,0,0,i,m - 1);
                Ans += solve(grid,i + 1,0,n - 1,j);
                Ans += solve(grid,i + 1,j + 1,n - 1,m - 1);
                ans = Math.min(ans,Ans);
            }

        for(int i = 0;i < n;i ++)
            for(int j = 0;j < m;j ++){
                //第三种情况
                int Ans = solve(grid,0,0,i,j);
                Ans += solve(grid,0,j + 1,n - 1,m - 1);
                Ans += solve(grid,i + 1,0,n - 1,j);

                ans = Math.min(ans,Ans);
            }

        for(int i = 0;i < n;i ++)
            for(int j = 0;j < m;j ++){
                //第四种情况
                int Ans = solve(grid,0,0,i,j);
                //System.out.println(solve(grid,0,0,i,j));
                Ans += solve(grid,0,j + 1,i,m - 1);
                //System.out.println(solve(grid,0,j + 1,i,m - 1));
                Ans += solve(grid,i + 1,0,n - 1,m - 1);
                //System.out.println(solve(grid,i + 1,0,n - 1,m - 1));
                ans = Math.min(ans,Ans);
            }
        for(int i1 = 0;i1 < n;i1 ++)
            for(int i2 = i1 + 1;i2 < n;i2 ++){
                int Ans = solve(grid,0,0,i1,m - 1);
                Ans += solve(grid,i1 + 1,0,i2,m - 1);
                Ans += solve(grid,i2 + 1,0,n - 1,m - 1);
                ans = Math.min(ans,Ans);
            }
        for(int j1 = 0;j1 < m;j1 ++)
            for(int j2 = j1 + 1;j2 < m;j2 ++){
                int Ans = solve(grid,0,0,n - 1,j1);
                Ans += solve(grid,0,j1 + 1,n - 1,j2);
                Ans += solve(grid,0,j2 + 1,n - 1,m - 1);
                ans = Math.min(ans,Ans);
            }
        return ans;
    }
    private int solve(int[][] grid,int x1,int y1,int x2,int y2){
        int n = grid.length, m = grid[0].length;

        boolean ok = true;
        int a = 15555,b = 15555,c = -1,d = -1;
        for(int i = x1;i <= x2;i ++)
            for(int j = y1;j <= y2;j ++) {
                if(grid[i][j] == 1) {
                    a = Math.min(a, i);
                    b = Math.min(b, j);
                    c = Math.max(c, i);
                    d = Math.max(d, j);
                    ok = false;
                }
            }
        if(ok) return 0;
        return (c - a + 1) * (d - b + 1);
    }
}
