class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:

        def solve(grid, rem):

            m = len(grid)
            n = len(grid[0])
            if rem == 1:
                minx = m-1
                maxx = 0
                miny = n-1
                maxy = 0
                noone = True
                for i in range(m):
                    for j in range(n):
                        if grid[i][j] == 1:
                            noone = False
                            minx = min(minx,i)
                            maxx = max(maxx,i)
                            miny = min(miny,j)
                            maxy = max(maxy,j)
                if noone:
                    return 0
                return max((maxx-minx+1) * (maxy - miny+1), 0)
            else:
                ans = m*n
                for i in range(1,m):
                    ans = min(ans, solve(grid[:i],1) + solve(grid[i:], rem-1))
                    ans = min(ans, solve(grid[i:],1) + solve(grid[:i], rem-1))
                for j in range(1,n):
                    og = []
                    ng = []
                    for k in range(m):
                        og.append(grid[k][:j])
                        ng.append(grid[k][j:])
                    ans = min(ans, solve(og,1) + solve(ng, rem-1))
                    ans = min(ans, solve(ng,1) + solve(og, rem-1))
                #print(grid, rem, ans)
                return ans
        return solve(grid,3)
