class Solution {
public:
    vector<vector<int>>v;
    int get(int l, int r, int u, int d) {
        int L = 1e9, R = -1, U = 1e9, D = -1;
        bool found = 0;
        for (int i = u; i < d; i++) {
            for (int j = l; j < r; j++) {
                if (v[i][j]) {
                    found = 1;
                    L = min(L, j);
                    R = max(R, j);
                    U = min(U, i);
                    D = max(D, i);
                }
            }
        }
        if (found)
            return (R - L + 1) * (D - U + 1);
        else
            return 0;
    }

    int ans = 1e9;

    int minimum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= m; j++) {

                int val1 = get(0, m , 0, i);
                int val2 = get(0, j, i, n);
                int val3 = get(j, m, i, n);
                if (i == 1) {
                }
                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);

                //-------------------


                val1 = get(0, j, 0, n);
                val2 = get(j, m, 0, i);
                val3 = get(j, m, i, n);

                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);

                //------------------


                val1 = get(0, m, n - i, n);
                val2 = get(0, j, 0, n - i);
                val3 = get(j, m, 0, n - i);

                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);


                //------------------


                val1 = get(m - j, m , 0, n);
                val2 = get(0, m - j , 0, i);
                val3 = get(0, m - j , i, n);

                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);

            }
        }
        for (int i = 0; i < m; i++) {
            for (int j = i + 1; j < m; j++) {
                int val1 = get(0, i , 0, n);
                int val2 = get(i, j, 0, n);
                int val3 = get(j, m, 0, n);

                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);
            }
        }

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                int val1 = get(0, m , 0, i);
                int val2 = get(0, m, i, j);
                int val3 = get(0, m, j, n);

                if (val1 && val2 && val3)
                    ans = min(ans, val1 + val2 + val3);
            }
        }
        return ans;
    }
    int minimumSum(vector<vector<int>>& grid) {
        v=grid;
        return minimum(grid);
    }
};
