int cols[31]={0},n,m;
vector<array<int,2>> rowfirst,colfirst;
int recTwo(vector<array<int,2>> points){
    int p=points.size();
    if(p==0) return 2;
    int fc=1e9,lc=-1;
    for(int i=0;i<=max(n,m);i++) cols[i]=0;
    for(auto u: points){
        cols[u[1]]++;
        fc=min(fc,u[1]);
        lc=max(lc,u[1]);
    }
    int ans=1e9,top=points[0][1],down=top;
    for(int i=0;i<p;i++){
        int row=points[i][0],col=points[i][1];
        top=min(top,col),down=max(down,col);
        int area=(row-points[0][0]+1)*(down-top+1);
        cols[col]--;
        while(cols[fc]==0 and fc<lc) fc++;
        while(cols[lc]==0 and lc>fc) lc--;
        if(i==p-1){
            ans=min(ans,area+1);
        }
        else{
            int l=fc,r=lc,u=points[i+1][0],d=points[p-1][0];
            area+=(r-l+1)*(d-u+1);
            ans=min(ans,area);
        }
    }
    return ans;
}

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        n=grid.size(),m=grid[0].size();
        int fc=-1;
        for(int j=0;j<m;j++){
            for(int i=0;i<n;i++)
                if(grid[i][j]==1){
                    fc=j;
                    break;
                }
                if(fc!=-1) break;
            }
        int ans=1e9;
        for(int sc=fc;sc<m;sc++){
            for(int fr=0;fr<n;fr++){
                for(int sr=fr;sr<n;sr++){
                    int area=(sr-fr+1)*(sc-fc+1);
                    rowfirst.clear();
                    colfirst.clear();
                    for(int i=0;i<n;i++)
                        for(int j=0;j<m;j++){
                            if(grid[i][j] and !(fr<=i and i<=sr and fc<=j and j<=sc)){
                                rowfirst.push_back({i,j});
                            }
                        }
                    for(int j=0;j<m;j++)
                        for(int i=0;i<n;i++){
                            if(grid[i][j] and !(fr<=i and i<=sr and fc<=j and j<=sc)){
                                colfirst.push_back({j,i});
                            }
                        }
                    int two=min(recTwo(rowfirst),recTwo(colfirst));
                    ans=min(ans,area+two);
                }
            }
        }
        return ans;
    }
};
