class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        int n = grid.size(), m = grid[0].size();

        auto getArea = [&](int left, int right, int bottom, int top) {
            int l = right, r = left, b = top, t = bottom;
            for(int i = left; i <= right; i++) {
                for(int j = bottom; j <= top; j++) {
                    if(grid[j][i] == 1) {
                        l = min(l, i);
                        r = max(r, i);
                        b = min(b, j);
                        t = max(t, j);
                    }
                }
            }
            return max(0, r - l + 1) * max(0, t - b + 1);
        };

        int ans = n * m;
        for(int i = 0; i < m - 2; i++) {
            for(int j = i + 1; j < m - 1; j++) {
                int area = getArea(0, i, 0, n - 1) + getArea(i + 1, j, 0, n - 1) + getArea(j + 1, m - 1, 0, n - 1);
                ans = min(ans, area);
            }
        }

        for(int i = 0; i < n - 2; i++) {
            for(int j = i + 1; j < n - 1; j++) {
                int area = getArea(0, m - 1, 0, i) + getArea(0, m - 1, i + 1, j) + getArea(0, m - 1, j + 1, n - 1);
                ans = min(ans, area);
            }
        }

        for(int i = 0; i < n - 1; i++) {
            for(int j = 0; j < m - 1; j++) {
                int area = getArea(0, m - 1, 0, i);
                area += getArea(0, j, i + 1, n - 1) + getArea(j + 1, m - 1, i + 1, n - 1);
                ans = min(ans, area);
            }


            for(int j = 0; j < m - 1; j++) {
                int area = getArea(0, m - 1, i + 1, n - 1) + getArea(0, j, 0, i) + getArea(j + 1, m - 1, 0, i);
                ans = min(ans, area);
            }
        }

        for(int i = 0; i < m - 1; i++) {
            for(int j = 0; j < n - 1; j++) {
                int area = getArea(0, i, 0, n - 1);
                area += getArea(i + 1, m - 1, 0, j) + getArea(i + 1, m - 1, j + 1, n - 1);
                ans = min(ans, area);
            }

            for(int j = 0; j < n - 1; j++) {
                int area = getArea(i + 1, m - 1, 0, n - 1);
                area += getArea(0, i, 0, j) + getArea(0, i, j + 1, n - 1);
                ans = min(ans, area);
            }
        }


        return ans;
    }
};
