class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {

        int m = grid.size(), n = grid[0].size();
        int ans = INT_MAX;

        for(int i = 0; i < m-1; i++)
        {
            ans = min(ans,calc_area(grid,0,i,0,n-1)+two_area(grid,i+1,m-1,0,n-1));
            ans = min(ans,two_area(grid,0,i,0,n-1)+calc_area(grid,i+1,m-1,0,n-1));
        }

        for(int i = 0; i < n-1; i++)
        {
            ans = min(ans,calc_area(grid,0,m-1,0,i)+two_area(grid,0,m-1,i+1,n-1));
            ans = min(ans,two_area(grid,0,m-1,0,i)+calc_area(grid,0,m-1,i+1,n-1));
        }

        return ans;

    }

    int two_area(vector<vector<int>>& grid, int a1, int a2, int b1, int b2)
    {
        if(a2 < a1 || b2 < b1) return 0;
        int ans = INT_MAX;
        for(int i = a1; i < a2; i++)
        {
            ans = min(ans,calc_area(grid,a1,i,b1,b2)+calc_area(grid,i+1,a2,b1,b2));
        }
        for(int i = b1; i < b2; i++)
        {
            ans = min(ans,calc_area(grid,a1,a2,b1,i)+calc_area(grid,a1,a2,i+1,b2));
        }

        return ans;
    }

    int calc_area(vector<vector<int>>& grid, int a1, int a2, int b1, int b2)
    {
        if(a2 < a1 || b2 < b1) return 0;
        int x1 = INT_MAX, x2 = -1, y1 = INT_MAX, y2 = -1;
        for(int i = a1; i <= a2; i++)
        {
            for(int j = b1; j <= b2; j++)
            {
                if(grid[i][j])
                {
                    x1 = min(x1,i);
                    x2 = max(x2,i);
                    y1 = min(y1,j);
                    y2 = max(y2,j);
                }
            }
        }
        if(x1 == INT_MAX) return 0;
        return (x2-x1+1)*(y2-y1+1);
    }

};
