class Solution {
public:
    int solve1(vector<vector<int>>& a, int il, int jl, int ir, int jr) {
        int cnt=0;
        int xmi=-1; int ymi=-1;
        int xma=-1; int yma=-1;

        for(int i=il; i<ir; i++) {
            for(int j=jl; j<jr; j++) {
                if (a[i][j]==1) {
                    cnt++;

                    if (xmi<0) {
                        xmi = xma = j;
                        ymi = yma = i;
                    } else {
                        xmi = min(xmi,j);
                        xma = max(xma,j);

                        ymi = min(ymi,i);
                        yma = max(yma,i);
                    }
                }
            }
        }

        if (!cnt) return 1e8;
        return (xma-xmi+1) * (yma-ymi+1);
    }

    int solve2(vector<vector<int>>& a, int il, int jl, int ir, int jr) {
        int ans = 1e8;
        {
            int sz = (ir-il) * (jr-jl);
            if (sz<2) return 1e8;
        }

        for(int j=jl; j<jr; j++) {
            ans = min(ans, solve1(a, il, jl, ir, j) + solve1(a, il, j, ir, jr) );
        }
        for(int i=il; i<ir; i++) {
            ans = min(ans, solve1(a, il, jl, i, jr) + solve1(a, i, jl, ir, jr) );
        }

        return ans;
    }

    int minimumSum(vector<vector<int>>& a) {
        int n = a.size();
        int m = a[0].size();

        int ans=n*m;

        for(int jr=0; jr<m; jr++) {
            ans = min(ans, solve1(a,0,0,n,jr) + solve2(a,0,jr,n,m) );
            ans = min(ans, solve2(a,0,0,n,jr) + solve1(a,0,jr,n,m) );
        }

        for(int ir=0; ir<n; ir++) {
            ans = min(ans, solve1(a,0,0,ir,m) + solve2(a,ir,0,n,m) );
            ans = min(ans, solve2(a,0,0,ir,m) + solve1(a,ir,0,n,m) );
        }

        return ans;
    }
};
