class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = m*n
        def calc(r1, c1, r2, c2):
            t, b, l, r = r2, r1, c2, c1
            flag = False
            for i in range(r1, r2+1):
                for j in range(c1, c2+1):
                    if grid[i][j] == 1:
                        flag = True
                        t = min(t, i)
                        b = max(b, i)
                        l = min(l, j)
                        r = max(r, j)
            return (b-t+1) * (r-l+1) if flag else inf

        # 1
        for r1 in range(m-2):
            s1 = calc(0, 0, r1, n-1)
            for r2 in range(r1+1, m-1):
                s2 = calc(r1+1, 0, r2, n-1)
                s3 = calc(r2+1, 0, m-1, n-1)
                ans = min(ans, s1 + s2 + s3)
        # 2
        for c1 in range(n-2):
            s1 = calc(0, 0, m-1, c1)
            for c2 in range(c1+1, n-1):
                s2 = calc(0, c1+1, m-1, c2)
                s3 = calc(0, c2+1, m-1, n-1)
                ans = min(ans, s1 + s2 + s3)

        # 3
        for r1 in range(m-1):
            s1 = calc(0, 0, r1, n-1)
            for c1 in range(n-1):
                s2 = calc(r1+1, 0, m-1, c1)
                s3 = calc(r1+1, c1+1, m-1, n-1)
                ans = min(ans, s1 + s2 + s3)

        # 4
        for r1 in range(1, m):
            s1 = calc(r1, 0, m-1, n-1)
            for c1 in range(n-1):
                s2 = calc(0, 0, r1-1, c1)
                s3 = calc(0, c1+1, r1-1, n-1)
                ans = min(ans, s1 + s2 + s3)

        # 5
        for c1 in range(n-1):
            s1 = calc(0, 0, m-1, c1)
            for r1 in range(m-1):
                s2 = calc(0, c1+1, r1, n-1)
                s3 = calc(r1+1, c1+1, m-1, n-1)
                ans = min(ans, s1 + s2 + s3)

        # 6
        for c1 in range(1, n):
            s1 = calc(0, c1, m-1, n-1)
            for r1 in range(m-1):
                s2 = calc(0, 0, r1, c1-1)
                s3 = calc(r1+1, 0, m-1, c1-1)
                ans = min(ans, s1 + s2 + s3)

        return ans
