import bisect
from typing import List


class Solution:
    def minimumSum(self, grid: List[List[int]]) -> int:
        m = len(grid)
        n = len(grid[0])
        ans = m * n

        h = set()
        s = set()
        for i, line in enumerate(grid):
            for j, num in enumerate(grid):
                if num == 1:
                    h.add(i)
                    s.add(j)

        def helper(left, right, up, down):
            small1 = down + 1
            small2 = right + 1
            big1 = up - 1
            big2 = left - 1
            for i in range(up, down + 1):
                line = grid[i]
                for j in range(left, right + 1):
                    if line[j]:
                        small1 = min(small1, i)
                        small2 = min(small2, j)
                        big1 = max(big1, i)
                        big2 = max(big2, j)
            if big1 >= small1:
                return (big1 - small1 + 1) * (big2 - small2 + 1)
            return 1

        for i in range(n - 2):
            for j in range(i + 1, n - 1):
                ans = min(ans, helper(0, i, 0, m - 1) + helper(i + 1, j, 0, m - 1) + helper(j + 1, n - 1, 0, m - 1))

        for i in range(m - 2):
            for j in range(i + 1, m - 1):
                ans = min(ans, helper(0, n - 1, 0, i) + helper(0, n - 1, i + 1, j) + helper(0, n - 1, j + 1, m - 1))

        for i in range(n - 1):
            for j in range(m - 1):
                ans = min(ans, helper(0, n - 1, 0, j) + helper(0, i, j + 1, m - 1) + helper(i + 1, n - 1, j + 1, m - 1))
                ans = min(ans, helper(0, n - 1, j + 1, m - 1) + helper(0, i, 0, j) + helper(i + 1, n - 1, 0, j))
                ans = min(ans, helper(0, i, 0, m - 1) + helper(i + 1, n - 1, 0, j) + helper(i + 1, n - 1, j + 1, m - 1))
                ans = min(ans, helper(i + 1, n - 1, 0, m - 1) + helper(0, i, 0, j) + helper(0, i, j + 1, m - 1))

        return ans
