class Solution {
    public int minimumSum(int[][] grid) {
        int d = grid.length - 1, r = grid[0].length - 1;
        int res = (d + 1) * (r + 1);
        for (int i = 0; i < d; i++) {
            res = Math.min(res, minimumArea(grid, 0, i, 0, r) + minimum2Area(grid, i + 1, d, 0, r));
            res = Math.min(res, minimum2Area(grid, 0, i, 0, r) + minimumArea(grid, i + 1, d, 0, r));
        }
        for (int j = 0; j < r; j++) {
            res = Math.min(res, minimumArea(grid, 0, d, 0, j) + minimum2Area(grid, 0, d, j + 1, r));
            res = Math.min(res, minimum2Area(grid, 0, d, 0, j) + minimumArea(grid, 0, d, j + 1, r));
        }
        return res;
    }

    public int minimum2Area(int[][] grid, int t, int d, int l, int r) {
        int res = (d - t + 1) * (r - l + 1);
        for (int i = t; i < d; i++) {
            res = Math.min(res, minimumArea(grid, t, i, l, r) + minimumArea(grid, i + 1, d, l, r));
        }
        for (int j = l; j < r; j++) {
            res = Math.min(res, minimumArea(grid, t, d, l, j) + minimumArea(grid, t, d, j + 1, r));
        }
        return res;
    }

    public int minimumArea(int[][] grid, int t, int d, int l, int r) {
        int h = 0, w = 0;
        int m = grid.length, n = grid[0].length;
        int s = -1;
        for (int i = t; i <= d; i++) {
            boolean flag = false;
            for (int j = l; j <= r; j++) {
                if (grid[i][j] == 1) {
                    flag = true;
                }
            }
            if (flag) {
                if (s == -1) {
                    s = i;
                    h = 1;
                } else {
                    h = i - s + 1;
                }
            }
        }
        s = -1;
        for (int j = l; j <= r; j++) {
            boolean flag = false;
            for (int i = t; i <= d; i++) {
                if (grid[i][j] == 1) {
                    flag = true;
                }
            }
            if (flag) {
                if (s == -1) {
                    s = j;
                    w = 1;
                } else {
                    w = j - s + 1;
                }
            }
        }
        return w * h;
    }
}
