class Solution {
public:
    vector <vector <int> > arr;
    int bounding(int u,int d,int l,int r){
        if (u>d||l>r) return 0;
        int mnr=d+1,mxr=u-1,mnc=r+1,mxc=l-1;
        for (int i=u; i<=d; i++){
            for (int j=l; j<=r; j++){
                if (arr[i][j]){
                    mnr=min(mnr,i);
                    mxr=max(mxr,i);
                    mnc=min(mnc,j);
                    mxc=max(mxc,j);
                }
            }
        }
        if (mnr>mxr) return 0;
        return (mxr-mnr+1)*(mxc-mnc+1);
    }
    int minimumSum(vector<vector<int>>& grid) {
        arr=grid;
        int n=grid.size(),m=grid[0].size();
        int ans=n*m;
        for (int i=0; i<n; i++){
            for (int j=i+1; j<n-1; j++){
                ans=min(ans,bounding(0,i,0,m-1)+bounding(i+1,j,0,m-1)+bounding(j+1,n-1,0,m-1));
            }
        }
        for (int i=0; i<m; i++){
            for (int j=i+1; j<m-1; j++){
                ans=min(ans,bounding(0,n-1,0,i)+bounding(0,n-1,i+1,j)+bounding(0,n-1,j+1,m-1));
            }
        }
        for (int i=0; i<n; i++){
            for (int j=0; j<m; j++){
                ans=min(ans,bounding(0,i,0,m-1)+bounding(i+1,n-1,0,j)+bounding(i+1,n-1,j+1,m-1));
                ans=min(ans,bounding(i+1,n-1,0,m-1)+bounding(0,i,0,j)+bounding(0,i,j+1,m-1));
                ans=min(ans,bounding(0,n-1,0,j)+bounding(0,i,j+1,m-1)+bounding(i+1,n-1,j+1,m-1));
                ans=min(ans,bounding(0,n-1,j+1,m-1)+bounding(0,i,0,j)+bounding(i+1,n-1,0,j));
            }
        }
        return ans;
    }
};
