class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto cut = [&] (int l0, int t0, int r0, int b0) {
            int l = r0;
            int t = b0;
            int r = l0;
            int b = t0;
            bool flag = false;
            for (int i = l0; i < r0; ++i) {
                for (int j = t0; j < b0; ++j) {
                    if (grid[i][j]) {
                        l = min(l, i);
                        t = min(t, j);
                        r = max(r, i);
                        b = max(b, j);
                        flag = true;
                    }
                }
            }
            if (flag) {
                return (r + 1 - l) * (b + 1 - t);
            }
            return 1000000;
        };
        int m = grid.size();
        int n = grid[0].size();
        int ans = INT_MAX;
        // - -
        for (int i = 0; i < m; ++i) {
            int t1 = cut(0, 0, i, n);
            for (int j = i; j < m; ++j) {
                int t2 = cut(i, 0, j, n);
                int t3 = cut(j, 0, m, n);
                ans = min(ans, t1 + t2 + t3);
            }
        }
        // | |
        for (int i = 0; i < n; ++i) {
            int t1 = cut(0, 0, m, i);
            for (int j = i; j < n; ++j) {
                int t2 = cut(0, i, m, j);
                int t3 = cut(0, j, m, n);
                ans = min(ans, t1 + t2 + t3);
            }
        }
        // - |
        for (int i = 0; i < m; ++i) {
            {
                int t1 = cut(0, 0, i, n);
                for (int j = 0; j < n; ++j) {
                    int t2 = cut(i, 0, m, j);
                    int t3 = cut(i, j, m, n);
                    ans = min(ans, t1 + t2 + t3);
                }
            }
            {
                int t1 = cut(i, 0, m, n);
                for(int j = 0; j < n; ++j) {
                    int t2 = cut(0, 0, i, j);
                    int t3 = cut(0, j, i, n);
                    ans = min(ans, t1 + t2 + t3);
                }
            }

        }
        // | -
        for (int i = 0; i < n; ++i) {
            {
                int t1 = cut(0, 0, m, i);
                for (int j = 0; j < m; ++j) {
                    int t2 = cut(0, i, j, n);
                    int t3 = cut(j, i, m, n);
                    ans = min(ans, t1 + t2 + t3);
                }
            }
            {
                int t1 = cut(0, i, m, n);
                for (int j = 0; j < m; ++j) {
                    int t2 = cut(0, 0, j, i);
                    int t3 = cut(j, 0, m, i);
                    ans = min(ans, t1 + t2 + t3);
                }
            }
        }
        return ans;
    }
};
