constexpr int MAX = 32;
constexpr int INF = 0x3F3F3F3F;

int mat[MAX][MAX];

int Find1(int r1, int c1, int r2, int c2) {
  int x1 = INF, y1 = INF, x2 = -INF, y2 = -INF;
  for (int i = r1; i < r2; ++i) {
    for (int j = c1; j < c2; ++j) {
      if (mat[i][j]) {
        x1 = min(x1, i);
        y1 = min(y1, j);
        x2 = max(x2, i);
        y2 = max(y2, j);
      }
    }
  }
  return x1 < INF ? (x2 - x1 + 1) * (y2 - y1 + 1) : 1;
}

int Find2(int r1, int c1, int r2, int c2) {
  int ret = INF;
  for (int i = c1 + 1; i < c2; ++i) {
    int l = Find1(r1, c1, r2, i);
    int r = Find1(r1, i, r2, c2);
    ret = min(ret, l + r);
  }
  return ret;
}

int Find3(int r1, int c1, int r2, int c2) {
  int ret = INF;
  for (int i = r1 + 1; i < r2; ++i) {
    int l = Find1(r1, c1, i, c2);
    int r = Find1(i, c1, r2, c2);
    ret = min(ret, l + r);
  }
  return ret;
}

class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
      int n = grid.size(), m = grid[0].size(), ret = INF;
      for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
          mat[i][j] = grid[i][j];
        }
      }
      for (int i = 1; i < n; ++i) {
        int v1 = Find1(0, 0, i, m);
        for (int j = i + 1; j < n; ++j) {
          int v2 = Find1(i, 0, j, m);
          int v3 = Find1(j, 0, n, m);
          ret = min(ret, v1 + v2 + v3);
        }
      }
      for (int i = 1; i < n; ++i) {
        int u1 = Find2(0, 0, i, m);
        int u2 = Find1(i, 0, n, m);
        int v1 = Find1(0, 0, i, m);
        int v2 = Find2(i, 0, n, m);
        ret = min({ret, u1 + u2, v1 + v2});
      }
      for (int i = 1; i < m; ++i) {
        int v1 = Find1(0, 0, n, i);
        for (int j = i + 1; j < m; ++j) {
          int v2 = Find1(0, i, n, j);
          int v3 = Find1(0, j, n, m);
          ret = min(ret, v1 + v2 + v3);
        }
      }
      for (int i = 1; i < m; ++i) {
        int u1 = Find3(0, 0, n, i);
        int u2 = Find1(0, i, n, m);
        int v1 = Find1(0, 0, n, i);
        int v2 = Find3(0, i, n, m);
        ret = min({ret, u1 + u2, v1 + v2});
      }
      return ret;
    }
};
