class Solution {
private:
    int minimumArea(vector<vector<int>>& grid, int aa, int ab, int ba, int bb) {
        int l1=INT_MAX, l2=INT_MAX, r1=0, r2=0;
        bool flag = true;
        for(int i=aa ; i<ba ; i++){
            for(int j=ab ; j<bb ; j++){
                if(grid[i][j]){
                    l1 = min(l1, i);
                    l2 = min(l2, j);

                    r1 = max(r1, i);
                    r2 = max(r2, j);
                    flag = false;
                }
            }   
        }
        if(flag){
            return 27000;
        }

        return (r1-l1+1)*(r2-l2+1);
    }
public:
    int minimumSum(vector<vector<int>>& grid) {
        int ans = INT_MAX;
        int N = grid.size(), M = grid[0].size();

        for(int i=1 ; i<N ; i++){
            for(int j=i+1 ; j<N ; j++){
                ans = min(ans, minimumArea(grid, 0, 0, i, M) + minimumArea(grid, i, 0, j, M) + minimumArea(grid, j, 0, N, M));
            }
        }
        for(int i=1 ; i<M ; i++){
            for(int j=i+1 ; j<M ; j++){
                ans = min(ans, minimumArea(grid, 0, 0, N, i) + minimumArea(grid, 0, i, N, j) + minimumArea(grid, 0, j, N, M));
            }
        }
        for(int i=1 ; i<N ; i++){
            for(int j=1 ; j<M ; j++){
                ans = min(ans, minimumArea(grid, 0, 0, i, j) + minimumArea(grid, 0, j, i, M) + minimumArea(grid, i, 0, N, M));
                ans = min(ans, minimumArea(grid, 0, 0, i, j) + minimumArea(grid, i, 0, N, j) + minimumArea(grid, 0, j, N, M));
                ans = min(ans, minimumArea(grid, 0, 0, N, j) + minimumArea(grid, 0, j, i, M) + minimumArea(grid, i, j, N, M));
                ans = min(ans, minimumArea(grid, 0, 0, i, M) + minimumArea(grid, i, 0, N, j) + minimumArea(grid, i, j, N, M));
            }
        }
        return ans;
    }
};
