func minimumArea(a [][]int, p, q int) (ans int) {
	l, r := int(1e9), 0
	up, down := int(1e9), 0
	for i, row := range a {
		for j, v := range row[p:q] {
			if v == 1 {
				l = min(l, j)
				r = max(r, j)
				up = min(up, i)
				down = max(down, i)
			}
		}
	}
	ans = (r - l + 1) * (down - up + 1)
	return
}

func minimumSum(a [][]int) (ans int) {
	ans = 1e9
	f := func() {
		n := len(a)
		m := len(a[0])
		if n < 2 {
			return
		}
		if n >= 3 {
			for i := 1; i < n; i++ {
				for j := i + 1; j < n; j++ {
					res := minimumArea(a[:i],0,m)
					res += minimumArea(a[i:j],0,m)
					res += minimumArea(a[j:],0,m)
					ans = min(ans, res)
				}
			}
		}
		if m < 2 {
			return
		}

		for j := 1; j < m; j++ {
			for i := 1; i < n; i++ {
				res := minimumArea(a[:i],0,j)
				res += minimumArea(a[:i],j,m)
				res += minimumArea(a[i:],0,m)
				ans = min(ans, res)

				res = minimumArea(a[:i],0,m)
				res += minimumArea(a[i:],0,j)
				res += minimumArea(a[i:],j,m)
				ans = min(ans, res)
			}
		}
	}

	f()
	rotateCopy := func(a [][]int) [][]int {
		n, m := len(a), len(a[0])
		b := make([][]int, m)
		for i := range b {
			b[i] = make([]int, n)
		}
		for i, r := range a {
			for j, v := range r {
				b[j][n-1-i] = v
			}
		}
		return b
	}
	a = rotateCopy(a)
	f()
	return
}
