class Solution {
public:
    int minimumSum(vector<vector<int>>& grid) {
        auto f = [&](int L, int R, int U, int D)
        {
            int l = R + 1, r = L, u = D + 1, d = U;
            for (int i = U; i < D; ++i)
            {
                for (int j = L; j < R; ++j)
                {
                    if (grid[i][j] > 0)
                    {
                        l = min(l, j);
                        r = max(r, j);
                        u = min(u, i);
                        d = max(d, i);
                    }
                }
            }

            if (l > r)
            {
                return 0;
            }

            return (r - l + 1) * (d - u + 1);
        };

        int n = grid.size();
        int m = grid[0].size();
        int ans = n * m;
        for (int i = 1; i < n; ++i)
        {
            for (int j = i + 1; j < n; ++j)
            {
                ans = min(ans, f(0, m, 0, i) + f(0, m, i, j) + f(0, m, j, n));
            }
        }

        for (int i = 1; i < m; ++i)
        {
            for (int j = i + 1; j < m; ++j)
            {
                ans = min(ans, f(0, i, 0, n) + f(i, j, 0, n) + f(j, m, 0, n));
            }
        }

        for (int i = 1; i < n; ++i)
        {
            for (int j = 1; j < m; ++j)
            {
                ans = min(ans, f(0, m, 0, i) + f(0, j, i, n) + f(j, m, i, n));
                ans = min(ans, f(0, m, i, n) + f(0, j, 0, i) + f(j, m, 0, i));
                ans = min(ans, f(0, j, 0, n) + f(j, m, 0, i) + f(j, m, i, n));
                ans = min(ans, f(j, m, 0, n) + f(0, j, 0, i) + f(0, j, i, n));
            }
        }

        return ans;
    }
};
